INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مهارت امید", "شاخص های سنجش سطح امید افراد", "خداوند زندگی راهی قرار داده که هر فرد  باید آن را طی کند. امید عامل حرکت همه افراد  در زندگی است. هنگامی که شخص به اندازه امید داشته باشد، دست یابی به موفقیت و پیشرفت برای او ساده می شود. بالا رفتن بیش از اندازه امید فرد را به غفلت می کشاند و زمینه نابودی خود را فراهم می نماید، پایین بودن امید نیز حرکت زندگی را  کُند می کند تا آن را به رکود بکشاند، رکودی که نتیجه اش یاس و ناامیدی می شود. هر گونه افراط و تفریط در امید، زندگی را دچار اختلال می شود. پرسشنامه پیشرو سعی در روشن کردن سطح مناسب و کارآمد امید بر اساس حقیقیترین، کاربردیترین  و مطمینترین منابع دارد.", 1, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("شهادت طلبی", "شاخص های سنجش شهادت طلبی", "شهادت بهترین رویارویی با مرگ است. مرگ پایان زندگی در دنیا و شروع زندگی ابدی است. کسانی که به استقبال شهادت می روند بهترین مرگ برای خود انتهاب می کنند. شهادت از چنان قدرتی برخوردار است که ایدیت را تامین می نماید. کسانی که شهید می شوند به قله نیکی ها دست پیدا کرده اند.", 1, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("شوهر خوب", "شاخص های سنجش شوهری", "خانواده کانون عبودیت الهی است. افرادی که زندگی خود را بر اساس دین الهی شکل داده اند، خانواده را به محلی برای شکوفایی استعدادهای همه اعضای آن تبدیل می کنند. در این میان شوهر، اصلی ترین رکن خانواده است که می تواند با اقداماتی ساده حرکت و سرعت تعالی خانواده را بالا ببرد.شوهر یکی از اعضای مهم خانواده است. اگر شوهر نباشد امکان تشکیل خانواده وجود ندارد. شوهر ظرفیت عظیمی برای گسترش توحید و سبک زندگی الهی دارد. هیچ چیزی نباید مانع ازدواج مردان و زنان مؤمن شود. هر مؤمنی باید به فکر این باشد که با ازدواج به مقام شوهری و همسرداری دست پیدا کند.", 2, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("همسر خوب", "شاخص های سنجش همسری", "خانواده کانون عبودیت الهی است. افرادی که زندگی خود را بر اساس دین الهی شکل داده اند، خانواده را به محلی برای شکوفایی استعدادهای همه اعضای آن تبدیل می کنند. در این میان شوهر، اصلی ترین رکن خانواده است که می تواند با اقداماتی ساده حرکت و سرعت تعالی خانواده را بالا ببرد.همسر یکی از ارکان کلیدی و مهم خانواده است. اگر همسر نباشد امکان تشکیل خانواده از بین می‌رود. همسر ظرفیت عظیمی برای گسترش توحید و سبک زندگی الهی است. خداوند برای هر شوهری از جنس خودش همسری را آفریده که می‌تواند در کنار آن آرامش و سکون را تجربه کند. همسر یکی از اعضای خانواده است که می‌تواند با کمک شوهر، سایر اعضای خانواده را نیز به وجود بیاورند.", 2, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("شخصیت های فعال و منفعل", "شاخص های تشخیص افراد فعال از منفعل", "در فرایند پذیرش، افراد یا فعالند یا منفعل. فعال ها به افرادی گفته‏ می‌شود که استعدادهای خود را بر اساس بینش و تفکر صحیح از خود و ابدیتشان مورد استفاده قرار‏ می‌دهند؛ کسانی که وقتی دست‌به‌کار‏ می‌شوند، کار را خیلی خوب و شایسته با به‌ کارگیری همه استعداد ها و فرصت‌ها به سرانجام‏ می‌رسانند؛ آنها در مسیر نجات هستند. افراد منفعل، یا استعدادهای خود را ناقص و به کندی به کار‏ می‌گیرند که امیدی به نجات خود دارند و یا با کوتاهی کردن و بدون تشخیص درست، استعداد های خود را مصرف‏ می‌کنند، کسانی که به دنبال بهانه‏ می‌گردند که از انجام کار فرار کنند؛ چنین افرادی در طول زندگی هزار و یک بهانه برای انجام ندادن‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌ها و عدم موفقیت خود دارند و عاقبت خطرناکی را برای ابدیت خود رقم‏ می‌زنند.", 3, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("روحیه ها", "شاخص های تشخیص روحیه های افراد", "روحیه‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های پنجگانه در همه افراد وجود دارد. هیچ فردی نیست مگر این‌که با یکی از روحیه‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌ها دست به اقدام یا ترکی‏ می‌زند. هر نوع کنش و واکنشی در افراد وابسته به روحیات آن‌ها می‌باشد. ممکن است در برخی از اوقات روحیه‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌ها تلفیق شوند اما معمولا در اقدامات و ترک‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های جزیی نیز روحیه‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌ها از هم تفکیک شده و مختلف هستند. پرسشنامه تشخیص روحیه‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌ها را باید در دوره‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های چند ماهه پر کرد چون روحیه‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های شخص در هر چند ماه تغییر کرده و متفاوت‏ می‌شود. شما دنیا را جای مناسبی برای تجارت‏ می‌دانید.", 3, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مرحله اول: انسانیت", "شاخص های تشخیص انسانیت", "مرحله اول رشد مرحله انسانیت است. فرد در این مرحله برخورداری خود از استعدادها و داشته‌ها را درک می‌کند. مرحله انسانیت را‏ می‌توان مرحله شناخت نیز نامید؛ چراکه اصلی‌ترین فعالیتی که هر فردی در این مرحله دارد شناخت داشته‌ها و ناداشته‌های خود است. خداوند در این مرحله برای انسان برخورداری‌ها و محرومیت‌هایی را فراهم‏ می‌نماید تا مسیر زندگی خویش را بهتر بشناسند.", 4, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مرحله دوم: اسلامیت", "شاخص های تشخیص اسلامیت", "مرحله بعد از انسانیت دوره اسلامیت است. مرحله اسلامیت مرحله پرهیز است. دوره اسلام دوره میانی رشد است. انسان‌هایی که توانایی‌ها و استعداهای خویش را شناخته‌اند و تصمیم به ساماندهی حرکت و زندگی خویش گرفته‌اند. مرحله اسلام، دومین مرحله‌ایی است که از آن عبور می‌کنند تا مراحل رشد را طی کنند. در مرحله اسلام فرد شروع به بهره‌برداری پرهیزکارانه از استعداد‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های خود می‌کند؛ یعنی علاوه بر این‌که از استعدادها به صورت صحیح استفاده‏ می‌نماید، تلاش می‌کند تا به دیگران نیز ضرری وارد نکند.", 4, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مرحله سوم : ایمانیت", "شاخص های تشخیص ایمان", "مرحله سوم مرحله ایمان است. مرحله ایمان بعد از مرحله اسلام قرار دارد . کسانی که به مدیریت خویش همت گمارده‌اند و دوران پرهیز و مرحله اسلام را طی‏ می‌کنند، وارد مرحله ایمان‏ می‌شوند. مرحله ایمان نیز مرحله بهره مندی از استعدادها است؛ اما تفاوتش با مراحل قبل این است که در استفاده از استعدادها توجه جدی به جلب اعتماد دیگران‏ می‌شود. در این مرحله ایمان وارد دل‌ها می‌شود و اطاعت از خداوند و پیامبرش باید در دستور کار قرار گیرد.", 4, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مرحله چهارم: تقوا", "شاخص های تشخیص تقوا", "مرحله چهارم از مراحل رشد تقوا است. تقوا بالاتر از ایمان قرار دارد. افرادی که در صدد صعود و رشد هستند باید مرحله تقوا را بگذرانند. مرحله تقوا نیز یکی دیگر از مراحل بهره مندی از استعدادها و داشته‌های افراد است. یکی از تفاوت‌های مهم مرحله تقوا با سایر مراحل قبل این است که تقوا بیشتر متمرکز بر عمل است؛ عمل محوری‌ترین عنصر تقوا است. فرد در تقوا همه برنامه‌های زندگی را اجرا می‌کند؛ یعنی دین تمام عرصه‌های زندگی افراد را در بر می‌گیرد.", 4, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مرحله پنجم: یقین", "شاخص های تشخیص یقین", "مرحله پنجم از مراحل رشد مرحله یقین است؛ مرحله‌ای که بالاترین مرحله رشد است. یقین مرحله‌ای از رشد است که هر فردی نمی‌تواند به آن مرحله برسد؛ البته همه افراد باید سعی خود را بکنند؛ اما در هر صورت افرادی که به مرحله یقین‏ می‌رسند از سایر افراد کمتر هستند. برنامه‌ها در مرحله یقین با مرحله قبل تغییر چندانی نمی‌کند؛ اما تفاوت مرحله یقین با سایر مراحل رشد این است که فرد، هر برنامه‌ای را که در دستور انجام یا ترک‏ می‌گذارد بدون هیچ شک و شبهه ای محقق‏ می‌کند. مرحله‌ای که فرد هر گامی را که بر‏ می‌دارد دارای یقین و دانایی و اعتماد و اطمینان کامل به برنامه، یعنی دین و برنامه‌ریز یعنی خداوند و فرستادگان و نمایندگان خداوند یعنی انبیا و امامان‏ می‌باشد", 4, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مرحله اول: شک", "شاخص های تشخیص شک", "اولین مرحله سقوط، مرحله شک است. فردی که با دعوت حق مواجه شده، در معرض خطر شک است. اگر شک بر انتخاب فرد تاثیر منفی بگذارد و در بینات یعنی روشن‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های روشن کننده شک کند احتمال سقوط در این مرحله زیاد‏ می‌شود؛ چنین افرادی دست از دلایل روشن برداشته و با دلایل خیلی ضعیف، دست از حق‏ می‌کشند. معمولا افرادی دچار شک‏ می‌شوند که یا تفکر نمی‌کنند یا صحیح تفکر نمی‌کنند. کسانی که معیارهای درست را به دست نیاورده‌اند و بر برخی از موضوعات تکیه‏ می‌کنند؛ موضوعاتی که ممکن است توسط عده‌ای از مردم نیز پذیرفته شده باشد؛ همین پذیرش عده‌ای زمینه شک را ایجاد می‌کند:", 5, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مرحله دوم: شرک", "شاخص های تشخیص شرک", "یکی دیگر از مراحل غی یا سقوط مرحله شرک است. شرک علاوه بر این‌که یک مرحله مستقل است و عده‌ای از افراد را در خود به سقوط‏ می‌کشاند با همه مراحل رشد نیز هم مرز است؛ یعنی فرد در هر مرحله‌ای از رشد که باشد امکان سقوط در شرک را دارد.
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مرحله سوم: کفر", "شاخص های تشخیص کفر", "یکی دیگر از مراحل غی و سقوط مرحله کفر است. امکان سقوط به کفر برای همه افراد رشد یافته وجود دارد؛ اما برای افرادی که در مرحله انسانیت هستند بیشتر است. سرانجام افرادی که در مرحله کفر قرار‏ می‌گیرند، همانند افرادی است که در مرحله شرک‏ می‌افتند؛ هر دو گروه در جهنم جا داده‏ می‌شوند آن‌ها از جمله بدترین موجودات روی زمین هستند؛ کسانی که برای همیشه در جهنم جا‏ می‌گیرند:", 5, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مرحله چهارم: نفاق", "شاخص های تشخیص نفاق", "یکی دیگر از مراحل غی نفاق است. نفاق برای افرادی زمینه غی را فراهم می‌کند که در مرحله اسلام یا ایمان پا گذاشته‌اند. خطر نفاق عموم انسان‌ها را تهدید می‌کند به خصوص افرادی را که از خودخواهی بیرون نیامده‌اند و همیشه با خودخواهی زندگی‏ می‌کنند زمینه نفاق بیشتری در آن‌ها شکل‏ می‌گیرد. آن‌ها حاضرند برای دست‌یابی به اهداف خود، دست به هر اقدامی بزنند.", 5, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مرحله پنجم: ارتداد و تعرب", "شاخص های تشخیص ارتداد و تعرب", "مرحله ارتداد و تعرب یکی دیگر از مراحل غی افراد رشد یافته به ویژه مسلمانان و مومنان است. ارتداد و تعرب یک نوع واپسگرایی، عقبگرد و دست کشیدن از دینداری است. بازگشت به جاهلیت است که فقط برای افراد رشد یافته به خصوص مسلمانان و مومنان رخ‏ می‌دهد. افرادی که وارد این مرحله‏ می‌شوند مجازات دنیوی و اخروی را برای خود رقم‏ می‌زنند. ارتداد بیشتر بعد از تقویت شهوات و شبهات در فرد شکل‏ می‌گیرد؛ اما تعرب بیشتر بعد از تغییر مکان زندگی یا بعد از مهاجرت رخ‏ می‌دهد. فرد در مکان جدید تحت تاثیر تفاوت‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های زندگی افراد آنجا دست به تعرب‏ می‌زند. افراد رشد یافته‌ای که ارتداد یا تعرب پیدا‏ می‌کنند، به دره ای سقوط کرده‌اند که هم در دنیا و هم در آخرت باید تاوانش را بپردازند. در این میان عده‌ای از آن‌هایی که مرتد و متعرب‏ می‌شوند به دنبال ضربه زدن به خدا و دین خدا دست به ارتداد و تعرب‏ می‌زنند؛ چنین افرادی فکر‏ می‌کنند که دینداری آن‌ها در پایداری دین و ماندگاری آن بسیار موثر است؛ به همین دلیل دست از دین برداشته تا به آن ضربه وارد کنند در صورتی که چنین نیست:", 5, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("سطح اول شکوفایی: تقوا", "شاخص های تشخیص متقین", "اولین و مهم‌ترین سطح شکوفایی استعدادها تقوا است. تقوا به معنای خویشتن بانی است. تقوا یعنی انجام خوب‌ها و ترک بدها، انجام واجب‌ها و ترک حرام‌ها.  تقوا یعنی هر فردی از استعدادهایش چنان استفاده کند که علاوه بر این‌که به خودش زیانی وارد نشود به دیگران نیز ضربه‌ای نزند. تقوا، مانند عبور فردی با لباس‌های بلند از میان بوته‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های خار و خاشاک است؛ به گونه‌ای که هیچ برخوردی با آن‌ها پیدا نکند. فردی که با چشم عبرت به گذشته زندگی افراد نگاه کند و با دقت پیروزی‌ها و ناکامی ‌‌‌‌‌‌‌‌‌‌‌‌‌‌های گذشتگان را بررسی کند، تقوا، مانع گرفتار شدن او در شبهات و کارهاى نسنجیده می‌شود.", 6, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("سطح دوم شکوفایی: احسان", "شاخص های تشخیص محسنین", "دومین سطح شکوفایی استعدادها، احسان است. احسان خوب انجام دادن خوبی‌ها و خوب ترک کردن بدی‌هاست. در این مرحله علاوه بر تقوا که انجام دادن و ترک کردن بود، روش نیز بهینه‏ می‌شود. رسیدن به سطح احسان رسیدن به زندگی ابدی است؛ هر محسنی همیشه زنده است اگر چه از دنیا برود؛ اما همیشه در حیات است. همه آن‌هایی که به سطح احسان‏ می‌رسند پاداش زندگی آن‌ها بهشت است", 6, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("سطح سوم شکوفایی: اخبات", "شاخص های تشخیص مخبتین", "سومین سطح شکوفاسازی استعدادها اخبات است. در این مرحله هر فردی از پیروزی‌ها و شکست‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌هایش سکویی برای شکوفایی استعدادهایش‏ می‌سازد. از ارتکاب بدی‌ها و انجام نیکی‌ها انکساری در او شکل‏ می‌گیرد که زمینه ارتقا را برایش فراهم می‌کند. در مواجهه با نیکی‌ها سعی می‌کند آن‌ها را متناسب با داشته‌های خود خالصانه انجام دهد و بعد از انجام نیکی‌ها با شکرگزاری زمینه ارتقا را فراهم می‌کند و در مواجهه با بدی‌ها سعی می‌کند تا پرهیز حداکثری داشته باشد و در صورتی که به هر گونه بدی آلوده شود، سعی می‌کند از آن توبه کند. در سطح اخبات فقط برنامه ارتقا مبتنی بر نیکی‌ها نیست بلکه علاوه بر نیکی‌ها و پیروزی‌ها از بدی‌ها و شکست‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌ها نیز پلی به سمت بالا و رشد ساخته‏ می‌شود. مخبتین اهل تسلیم هستند.", 6, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("سلامت حال", "شاخص های تشخیص سطح سلامت حال افراد", "بهبود و سلامت حال افراد دارای شاخص‌هایی است و قابلیت ارزیابی دارد. کسانی که اهل طاعتند و برای خدا و طبق قوانین الهی، انجام‌ها و ترک‌های خود را سامان می‌دهند، حالشان از سلامت و بهبود خوبی برخوردار است؛ کسانی که از خدا و رسولش اطاعت کنند، خداوند آینده خوبی برای آن‌ها رقم می‌زند؛ آن‌ها را در بهشت‌هایی جای‏ می‌دهد که از زیر درختانشان نهرها جاری است؛ ابدیت آن‌ها برای همیشه در بهشت ادامه پیدا خواهد کرد؛ از جمله کسانی که توانستند، این چنین زندگی کنند و با اطاعت خداوند و رسولش ابدیت خود را سامان دهند، فرستادگان خداوند، راستگویان، شهداء و نیکوکاران هستند", 7, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("وخامت حال", "شاخص های تشخیص سطح وخامت افراد", "وخامت حال افراد دارای شاخص‌هایی است و قابلیت ارزیابی دارد. کسانی که اهل گناهند و قوانین الهی را زیر پا می‌گذارند و توجهی به فرامین فرستادگان خداوند ندارند حالشان وخیم شده و زمینه خالد ماندن در جهنم را برای خود مهیا کرده‌اند پس گناهکاران در هر سطحی از وخامت حال که باشند باید سعی کنند تا وضعیت خود را بهبود دهند", 7, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مراتب ارتکاب معاصی", "شاخص های تشخیص مرتبه ارتکاب معاصی", "ارتکاب گناهان در مراتب مختلف است. مراتبی که بر اساس شدت و ضعف یا کاهش و افزایش وخامت حال گناهکاران طراحی شده است. آلودگی به گناه فرد که در مرتبه اولیه ارتکاب به گناه است آثار تخریبی کمتری نسبت به فرد دارد که در مراتب نهایی ارتکاب به گناه قرار دارد. هر چه مراتب ارتکاب گناهان بالاتر برود قدرت تخریب و اثر گذاری گناهان بیشتر می شود. در مرتبه عادی گناه بودن برای فرد نشان دهنده مشیت خداوند بر قبول ساده توبه و بازگشت او است؛ برخلاف فردی که از مرتبه عادی عبور کرده و به مرتبه استدراج رسیده است.", 7, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مهارت مدارا", "شاخص های سنجش مدارا", "با هم بودن و با هم زندگی کردن فرصت بزرگی است که خداوند در اختیار آحاد افراد و جوامع قرار داده است. زندگی مبتنی بر سبک استکباری که اكنون در بسیاری از جوامع رایج است نتیجه‌ای جز گسترش بی‌اعتمادی و خشونت را در پی ندارد. خداوند در قالب دین برنامه ای برای زندگی در اختیار بشر قرار داده است تا بتواند با تحقق آن به شکوفاسازی استعدادهای خود بپردازد. مدارا زمینه شکوفاسازی استعدادها را در جامعه فراهم می‌نماید.", 1, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("پیشگیری از خودکشی", "شاخص های تشخیص احتمال سطح خودکشی", "خودکشی در شمار بدترین کارهایی است که فرد می تواند انجام دهد. خودکشی نتیجه حاکمیت مستکبران در دنیا است. هر چقدر حکومت مستکبران طولانی تر می شود خودکشی افزایش می یابد. افزایش خودکشی، گسترش فرهنگ نابودگری است؛ فرهنگی که نه تنها حال و آینده افرادِ خودکُش  را از بین می برد بلکه فرهنگ ناامیدی و پوچی را تقویت می کند. در جوامع کنونی که حکومت ها توان گسترش عدالت را ندارند و ظلم بیداد می کند و همه جا را به تاریکی می کشاند، خودکشی آخرین و در دسترس ترین راه حلی است که به ذهن افراد می رسد.", 8, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("مربی فرزندان خود باشید هفت سال اول", "شاخص های سنجش تربیت فرزندان", "یکی از مراحل مهم تربیت، مرحله ارزیابی تربیت است. در مراحل و سنین مختلف، والدین نگران آن هستند که تربیت به درستی شکل گرفته  یا نه؟ فعالیت ها و اقداماتی که تا به حال انجام شده، صحیح بوده یا نه؟ تاثیر آنها تا چه اندازه مفید بوده است؟ و سوالات دیگری که نشان دهنده نگرانی والدین است. شاخص‏های سنجش، برای ارزیابی تربیت است. والدین با در دست داشتن معیارهای سنجش می توانند تربیت خود را در هر مرحله مورد ارزیابی قرار دهند تا با نقاط قوت و ضعف آن آشنا شوند.", 2, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("انتظار", "شاخص های سنجش سطح انتظار آخرین منجی", "مردمی که در دوره قبل از تحقق وعده الهی زندگی می¬کنند منتظر موعود الهی هستند. انتظار یعنی منتظر تحقق وعده الهی ماندن است. آخرین موعود الهی همانند تمام موعودهای قبلی خواهد آمد. عده ای از مردم گمان می کنند که انتظار دست روی دست گذاشتن است در صورتی که انتظار یعنی عمل به تکالیف و فرامین الهی است. انتظار پشت گوش انداختن و تنبلی نیست انتظار تصمیم گرفتن و برنامه ریزی دقیق است. انتظار بی خیالی و کنار کشیدن نیست انتظار فعال شدن و در میدان آمدن است.", 9, 1);
INSERT INTO `tests` (`title`, `shortDescriptions`, `longDescriptions`, `category_id`, `extradata_id`) VALUES ("آخرین منجی", "شاخص های تشخیص آخرین منجی", "منجی آخرالزمان نماینده خداوند است. نمایندگان خداوند افرادی هستند که از طرف خداوند برای رساندن سخنان او به مردم فرستاده شدند، افرادی که با کمک مردم با حکومت های طاغوتی مبارزه کرده و اقدام به برپایی حکومتی عادلانه می کنند تا زمینه شکوفایی استعداد آحاد مردم به ویژه مستضعفان فراهم شود.", 9, 1);
