INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر امیدی که در دل دارید همراه با ترس است؟", "امام رضا7فرمودند: هر كس به چيزى اميدوار باشد او را طلب مى‏كند، و هر كس از چيزى بترسد از آن فرار مى‏كند، هر بنده‏اى كه در دلش خوف و رجاء باشد خداوند همه آرزوهاى او را بر آورده مى‏كند و او را از هر چه مى‏ترسد آسايش مى‏دهد.", 1, 1);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("امید و ترس در دل شما با هم توازن دارند؟", "امام صادق7فرمودند: لقمان در ضمن وصاياى خود به پسرش ناتان گفت پسرم در ضمن ساز و برگ براى برابرى با دشمن خود با او در آميز و اظهار رضايت از او بكن و از او دورى مكن تا درونت را بداند و براى برابرى با تو آماده گردد اى پسر جانم از خدا چنان بترس كه اگر عبادت ثقلين را به آستانش ببرى نگران از عذابش باشى و به او چنان اميدوار باش كه اگر گناه ثقلين را به آستانش ببرى اميد آمرزش از او دارى، پسر جان، من سنگ و آهن و هر بار ثقيلى بدوشم كشيدم و سنگين‏تر از همسايه بد نديدم هر تلخى را چشيدم و تلختر از فقر نيافتم.", 1, 2);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر در هنگام امیدواری با امید به خداوند احتمال به دست آوردن خواسته خود را بالاتر می دانید؟", "شخصى به امام صادق7عرض كرد: من گناه مى‏كنم و بعد ناراحت مى‏شوم در عين حال اميد عفو دارم؟ فرمود: فلانى از خدا بترس و عبادتش بكن و در عين حال اميد قبولى داشته باش، زيرا خوش‏گمان‏ترين فرد به خدا و بزرگترين اميدوار كسى است كه بيشتر به طاعت عمل كند و پيامبر اسلام  و امیر مومنان7از همه مردم حسن ظنّ بيشترى به خدا داشتند و از همه اميدوارتر بودند و بيش از همگان از خدا مى‏ترسيدند، و هيبت و ترس او در دلشان بود و همچنين ساير پيامبران  بر مردم زمان خود در همه اين امور افضل بودند.", 1, 3);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("خواسته های شما رو به کاهش است یا افزایش؟", "امام صادق7فرمودند: دليل خوف آن است كه آدمى از گناهان بگريزد، دليل رجاء آن است كه انسان دنبال مقصود برود.", 1, 4);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("خواسته های شما موقتی است یا دائمی؟", "يكى از اصحاب گويد: به امام صادق7عرض¬كردم: مردمى هستند كه گناه مي¬كنند و مي¬گويند ما اميدواريم (برحمت خدا) و همواره چنينند تا مرگشان فرا مي¬رسد (يعنى توبه هم نميكنند) فرمود:اين¬ها مردمى باشند كه در ميان آرزوها ميلولند، دروغ گويند، اين¬ها اميدوار نيستند، هر كه به چيزى اميدوار باشد آن را طلب كند و هر كه از چيزى بترسد از آن بگريزد.", 1, 4);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("امیداوری در میزان دعا کردن شما تاثیری دارد؟", "امام صادق7 فرمودند: مؤمن هماره در خير و خوشى است. و اميدوار به رحمتى كه از جانب حق نازل شود، در صورتى كه شتاب نورزد و از اجابت دعا نوميد نشود كه ديگر دعا نكند. من گفتم: چگونه شتاب مى‏ورزد؟ ابو عبد اللَّه گفت: با خودش مى‏گويد: مدت¬ها است كه دعا مى‏كنم و اجابتى نمى‏بينم.", 1, 5);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("دعا کردن شما همیشگی است یا موقتی؟", "امام صادق7 فرمودند: مؤمن هماره در خير و خوشى است. و اميدوار به رحمتى كه از جانب حق نازل شود، در صورتى كه شتاب نورزد و از اجابت دعا نوميد نشود كه ديگر دعا نكند. من گفتم: چگونه شتاب مى‏ورزد؟ ابو عبد اللَّه گفت: با خودش مى‏گويد: مدت ها است كه دعا مى‏كنم و اجابتى نمى‏بينم.", 1, 5);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("افزایش امیدواری شما چه تاثیری در تلاشگری شما دارد؟", "یقیناً کسانی که ایمان آورده، و آنان که هجرت کرده و در راه خدا به جهاد برخاستند، به رحمت خدا امید دارند؛ و خدا بسیار آمرزنده و مهربان است.", 1, 6);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("با افزایش امیدتان چقدر علاقمند به تلاش و فعالیت¬های خوبتان می¬شوید؟", "امیرمومنان7فرمودند : تقوای الهی داشته باشید، تقوای کسی که از ترس بدنش کوفته وخسته باشد و شب زنده داری خواب را از چمشش ربوده است وامید به رحمت الهی اورا به تشنگی روزه های گرم واداشته است.", 1, 7);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("برای ساخت آینده بیشتر به اعمال نیکی که انجام داده اید امیدواری یا رحمت الهی؟", "امام صادق7فرمودند: خدایا مغفرت تو وسیعتر از گناهان من است و رحمت تو امیدوار کننده تر است نزد من از عملم، پس بیامرز برای من ای زنده و این کسی که نمیمیری.", 1, 8);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در چه مواقعی مراقب چگونگی استفاده از نعمت های در اختیار خود هستید؟", "أمیرمؤمنان7فرمودند : تا وقتی شتر زمانه رام توست کارها را آسان گیر ونعمتی را به امید زیاده خواهی در خطر میفکن.", 1, 9);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("امیدورای شما در امور دنیوی است یا اخروی؟", "أمیرمؤمنان7فرمودند: كسى به گمان خود ادّعا دارد كه به خداوند اميد دارد، سوگند به خداى بزرگ كه دروغ مى‏گويد. او را چه شده كه اميدوارى‏اش در عملش آشكار نيست؟! هر اميد دارنده‏اى اثر اميدش در عملش پيداست مگر اميدى كه مردم به خداى تعالى دارند كه اين اميد داراى عيب و ناخالص است، و هر ترسى [اثرش‏] مسلّم و ثابت است مگر ترس از خدا كه اين ترس [در بيشتر مردم‏] ناقص و بيمار است، در كار بزرگ [آخرت‏] به خدا اميد دارد و در كار كوچك [دنيا] به بندگان خدا اميدوار است، پس با بنده خدا به گونه‏اى رفتار مى‏كند كه با خداوند چنين رفتارى ندارد، پس چگونه است شأن خداوند جلّ ثنائه‏ كه در حقّ او نسبت به آنچه كه با بندگان رفتار مى‏شود كوتاهى مى‏گردد؟ آيا مى‏ترسى كه در اميدواريت به خدا دروغگو باشى يا اينكه او را شايسته اميدوارى نمى‏بينى؟ همچنين اگر از بنده‏اى بترسد چنان از او مى‏ترسد كه از پروردگارش به اين اندازه نمى‏ترسد. پس ترس از بندگان را نقد و ترس از آفريننده‏اش را به منزله وامى كه اميد برگشت آن نيست و وعده‏اى كه به آن عمل نشود مى‏پندارد.", 1, 10);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر امیدواری شما در توجه و هوشیاریتان در زندگی تأثیرگذار است؟", "لقمان حکیم;به پسرش گفت: اى فرزندم! داراى دو قلب باش، قلبى كه بوسيله آن چنان از خدا بترسى كه هيچ گاه بر آن ترس، كاستى راه ندارد، و قلبى كه به خدا آنچنان اميدوار باشى كه غفلت در آن راه ندارد.", 1, 11);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("امید شما چه تأثیری در میزان گناهانتان در زندگی دارد؟", "امام صادق7 فرمودند:چندان به خداوند اميدوار باش كه (اين اميد) تو را به نافرمانى او نكشاند و چندان از او بيم دار كه (اين بيم) تو را از رحمت او نوميد نسازد.", 1, 12);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر ارتکاب به گناهان را در شرایط زندگی خود تأثیر گذار می دانید؟", "لقمان حکیم; به پسرش فرمود: ای فرزندم بترس از خدا ترسی که از رحمتش مایوس نشوی و امید داشته باش به او امیدی که که خود را از مکرش در امان نبینی.", 1, 13);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("کدام یک از إطمینان و امید را بر هم دیگر ترجیح می دهید؟", "امام موسی کاظم7فرمودند: ... اى هشام عاقل بكسى كه بترسد تكذيبش كند خبر ندهد و از آنكه نگرانى مضايقه دارد چيزى نخواهد و بآنچه توانا نيست وعده ندهد و به آنچه در اميدواريش سرزنش شود اميد نبندد و بكارى كه بترسد در آن درماند اقدام نكند.", 1, 14);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در چه شرایطی از زندگی امیدوار هستید؟", "امیرمؤمنان7به مردى فرمود: شما چگونه‏ايد؟ گفت: اميدواريم و بيمناك. حضرت  فرمود: كسى كه به چيزى اميد بسته در جستجويش برآيد و كسى كه از چيزى بيم دارد از آن بگريزد. من نمى‏دانم ترس و بيم مردى كه با شهوتى مواجه شود و در اثر ترس و بيم از آن دست نكشد چه مفهومى دارد! و نمى‏دانم اميد مردى كه بلايى بر او نازل شود و او در اثر اميد بر آن شكيبايى نكند چگونه اميدى است.", 1, 15);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما خود را فرد خیال بافی می دانید؟", "امام باقر7 فرمودند:مبادا كه اميدى دروغين ببندى، كه تو را در بيمى راستين افكند.", 1, 16);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چگونه به چیزی یا فردی امیدوار می شوید؟", "أمیرمؤمنان7فرمودند : چه بسا ناامیدی ها که به خاطر آرزوهایی دروغ بوده است.", 1, 16);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در هر روز چند بار به شهادت فکر می‌کنید؟", "امام على عليه السلام‏ آن جا كه ياران خود را به دليل سستى در امر جهاد توبيخ كرد- فرمود: براى من محبوبترين چيزى كه ملاقاتش كنم، مرگ است.", 2, 21);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در هر ماه چند بار اشتیاق به شهادت در شما روشن می شود؟", "امام على عليه السلام‏ آن جا كه ياران خود را به دليل سستى در امر جهاد توبيخ كرد- فرمود: براى من محبوبترين چيزى كه ملاقاتش كنم، مرگ است.", 2, 17);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در هر ماه چند بار در مورد اشتیاق به شهادت با اطرافیان گفتگو می کنید؟", "امام على عليه السلام‏ آن جا كه ياران خود را به دليل سستى در امر جهاد توبيخ كرد- فرمود: براى من محبوبترين چيزى كه ملاقاتش كنم، مرگ است.", 2, 17);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("دوست دارید خودتان شهید شوید؟", "امام على عليه السلام‏- آن جا كه ياران خود را به دليل سستى در امر جهاد توبيخ كرد- فرمود: براى من محبوبترين چيزى كه ملاقاتش كنم، مرگ است.", 2, 17);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("دوست دارید اطرافیانی که به‌ آن‌ها علاقه دارید شهید شوند؟", "امام على عليه السلام‏- آن جا كه ياران خود را به دليل سستى در امر جهاد توبيخ كرد- فرمود: براى من محبوبترين چيزى كه ملاقاتش كنم، مرگ است.", 2, 17);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("وقتی یاد شهادت می کنید چقدر نسبت به آینده خانواده به خداوند اطمینان دارید؟", "امام رضا (ع) فرمود كه حسين بن على (ع) فرموده: ... و خداى عزّ و جلّ مى‏فرمايد: من جانشين شهيد در ميان خانواده او هستم. و هر كس خانواده شهيد را خشنود كند، هر آينه مرا خشنود كرده است، و هر كس‌ آن‌ها را خشمگين كند، همانا مرا خشمگين كرده است... .", 2, 18);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("هنگام انجام کار خیر چقدر نگران ضرری هستید که ممکن است بعد از انجام آن کار خیر به زندگی شما وارد شود؟", "امام رضا (ع) فرمود كه حسين بن على (ع) فرموده: ... و خداى عزّ و جلّ مى‏فرمايد: من جانشين شهيد در ميان خانواده او هستم. و هر كس خانواده شهيد را خشنود كند، هر آينه مرا خشنود كرده است، و هر كس‌ آن‌ها را خشمگين كند، همانا مرا خشمگين كرده است... .", 2, 18);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("وقتی به کار خیری اقدام می کنید چقدر سعی در اتمام آن دارید؟", "امام رضا (ع) فرمود كه حسين بن على (ع) فرموده: ... و خداى عزّ و جلّ مى‏فرمايد: من جانشين شهيد در ميان خانواده او هستم. و هر كس خانواده شهيد را خشنود كند، هر آينه مرا خشنود كرده است، و هر كس‌ آن‌ها را خشمگين كند، همانا مرا خشمگين كرده است... .", 2, 18);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("به نظر شما شهادت چقدر در سطح کمی و کیفی زندگی خانواده اش تاثیر دارد؟", "امام رضا (ع) فرمود كه حسين بن على (ع) فرموده: ... و خداى عزّ و جلّ مى‏فرمايد: من جانشين شهيد در ميان خانواده او هستم. و هر كس خانواده شهيد را خشنود كند، هر آينه مرا خشنود كرده است، و هر كس‌ آن‌ها را خشمگين كند، همانا مرا خشمگين كرده است... .", 2, 18);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("به نظر شما اگر شهید به شهادت نرسیده بود، سطح کمی و کیفی زندگی خانواده‌اش بهتر می‌شد؟", "امام رضا (ع) فرمود كه حسين بن على (ع) فرموده: ... و خداى عزّ و جلّ مى‏فرمايد: من جانشين شهيد در ميان خانواده او هستم. و هر كس خانواده شهيد را خشنود كند، هر آينه مرا خشنود كرده است، و هر كس‌ آن‌ها را خشمگين كند، همانا مرا خشمگين كرده است... .", 2, 18);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما در میان خانواده، خویشاوندان، جایگاه اجتماعی، شغل و منزلتان، خداوند، پیامبر اسلامو جهاد کدام را بیشتر دوست دارید؟", "بگو: اگر پدرانتان و فرزندانتان و برادرانتان و همسرانتان و خویشانتان و اموالی که فراهم آورده‌اید و تجارتی که از بی رونقی و کسادی اش می‌ ترسید و خانه هایی که به‌ آن‌ها دل خوش کرده اید، نزد شما از خدا و پیامبرش و جهاد در راهش محبوب ترند، پس منتظر بمانید تا خدا فرمان عذابش را بیاورد؛ و خدا گروه فاسقان را هدایت نمی‌ کند.", 2, 19);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("وقتی به جهاد فکر می‌کنید چقدر نگران از دست دادن شغل و موقعیت اجتماعی خودتان هستید؟", "بگو: اگر پدرانتان و فرزندانتان و برادرانتان و همسرانتان و خویشانتان و اموالی که فراهم آورده‌اید و تجارتی که از بی رونقی و کسادی اش می‌ ترسید و خانه هایی که به‌ آن‌ها دل خوش کرده اید، نزد شما از خدا و پیامبرش و جهاد در راهش محبوب ترند، پس منتظر بمانید تا خدا فرمان عذابش را بیاورد؛ و خدا گروه فاسقان را هدایت نمی‌ کند.", 2, 19);
