INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("با چه انگیزه ای به آرزوی شهادت می کنید؟ به دلیل خستگی از وضعیت زندگی و عدم توان در تغییر آن یا برای رسیدن به فوز بزرگ شهادت و همجوار شدن با خوبان؟", "پیامبر فرمود: هیچ کسی وارد بهشت نمی شود که آرزوی بیرون رفتن از آن بکند مگر شهید. او آروز می کند که ای کاش به دنیا برمی گشت و ده بار دیگر شهید می شد، به دلیل چیزهایی که از کرامت خداوند دیده است.", 2, 20);
