INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر به فکر ادا کردن حقوق همسر خود هستید؟", "امیر مؤمنان(ع) فرمودند: به خدا سوگند که تا (فاطمه) زنده بود، نه او را عصبانی نمودم و نه به کاری مجبورش کردم. او نیز، نه مرا عصبانی نمود و نه در کاری از من نافرمانی کرد. هنگامی که به او نگاه می کردم، غم ها و اندوه ها از من برطرف می شدند.", 3, 24);
