INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("غالبا وقتی برخوردار‏ می‌شوید چه احساسی در شما ایجاد می‌شود؟", "این چنین نیست [که انسان سپاس گزار باشد] مسلماً انسان سرکشی می  کند.(6) برای اینکه خود را بی نیاز می  پندارد.(علق، 7)", 7, 50);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("به هدفمندی دنیا و پاسخگو بودن بعد از آن اعتقاد دارید؟", "و انسان می‌گوید: آیا زمانی که بمیرم به راستی زنده [از خاک] بیرون آورده می‌شوم؟(مریم، 66)", 7, 51);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("سطح امیدواری در شما چقدر است؟", "و اگر از سوی خود رحمتی [چون سلامتی، ثروت، اولاد و امنیت] به انسان بچشانیم، سپس آن را از او سلب کنیم، بسیار ناامید شونده و بسیار ناسپاس است.(هود، 9)", 7, 52);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("سرعت نا امید شدن در شما چقدر است؟", "و اگر از سوی خود رحمتی [چون سلامتی، ثروت، اولاد و امنیت] به انسان بچشانیم، سپس آن را از او سلب کنیم، بسیار ناامید شونده و بسیار ناسپاس است.(هود، 9)", 7, 52);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("واکنش شما در برابر نیکی‌های دیگران چگونه است؟", "قطعاً انسان نسبت به پروردگارش بسیار ناسپاس است،(عادیات، 6)", 7, 53);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چه اقدامی نسبت به نیاز فقرای فامیل دارید؟", "از سماعة (بن مهران) نقل شده است که گفت: از آن حضرت سؤال کردم در باره گروهى که وسایل اضافى دارند، در حالى که برادرانشان سخت نیازمندند؛ آن گروه زکات ندارند که برادرانشان را سیر و از آن‌ها رفع نیاز کنند؛ زمان بسیار سختی است. حضرت فرمودند: مسلمان برادر مسلمان است؛ نه به او ظلم مى‏کند و نه ذلیل و محرومش مى‏نماید. وظیفه مسلمانان این است که با مهربانى براى رفع نیاز آن‌ها تلاش نمایند و با آن‌ها مواسات داشته باشند. عشق ورزى شما نسبت به ایشان یک وظیفه الهى است که بر یک‌دیگر مهربان و دلسوز باشید؛ همان طورى که گذشتگان از انصار در زمان حضرت رسول چنین بودند.", 8, 54);
