INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر نسبت به تاثیر اعمال در زندگی و ابدیت توجه دارید؟", "حضرت صادق از امام على نقل می‌کنند که فرمودند: آنچه باعث ضرر و زیان در قیامت مى‏شود نباید کوچک شمرده شود و همچنین آنچه نفعش در آن روز به دست انسان می‌رسد، نباید بى‏ارزش دانسته شود و شما در مطالب و حقایقى که خدا خبر داده مانند کسى باشید که با چشم خود آنها را دیده و معاینه کرده است.", 11, 94);
