INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر در انجام اعمال خیر تداوم دارید؟", "وهب بن منبه، نقل كرده: كه من در تورات چگونگى آفرينش آدم را يافتم كه خداوند هنگام خلق و ابداع او چكار كرده. خداوند فرموده: ...‏از مداومت بر خیر [نه] شعبه منشعب مى‏شود؛ صلاح، اقتدار، عزّت داشتن، اخبات، توبه نمودن، مهترى و بزرگى، امنیت داشتن، خشنودى مردم از او، حسن عاقبت... .", 19, 103);
