INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("برای اثبات خوبی خود چه اقدامی را انجام می‌دهید؟", "حضرت صادق فرمودند: شخص مغرور در دنیا فقیر است و در آخرت مغبون خواهد بود؛ زیرا که او آخرت خود را که برتر است به دنیاى پست فروخته است و نسبت به نفس خود دچار عجب نشو؛ چه بسا به وسیله مال خود و یا بهبودى و تندرستى مزاج خود مغرور مى‏شوى و تصور مى‏کنى که به خاطر این وسیله زندگى تو ادامه پیدا کرده و باقى خواهى بود. و چه بسیار مى‏شود که از جهت طول عمر خود و یا فرزندان و یاران خود مغرور مى‏شوى و خیال مى‏کنى‏ که آن‌ها وسیله نجات و خلاصى تو خواهند بود. و گاهى از لحاظ زیبایی ظاهر و مکان رشد و آرزوهاى بر آورده شده و تمایلات و خواهش‌هاى نفسانى خود که به دست آوردى مغرور مى‏شوى و گمان مى‏برى که تو در راه صدق و حق هستى. و گاهى به خاطر آنچه در مقابل مردم اظهار توبه و ندامت کرده و بر اعمال بد خود پشیمان مى‏شوى، مغرور مى‏شوى و خیال مى‏کنى که همین اندازه براى مقام عبودیت و انجام وظایف طاعت کافى است؛ در صورتى که ممکن است باطن و قلب تو بر خلاف اظهار و زبان تو باشد. و گاهى خود را به عبادت وامى‏دارى و از روى تکلف عبادت مى‏کنى؛ در صورتى که مطلوب نزد پروردگار متعال اخلاص در عبادت است. و چه بسا به وسیله علم و نسبی که داری به خود بالیده و مغرور مى‏گردى؛ در صورتى که از باطن و از غیب و نتیجه جریان کارهاى خود غافل هستى. و گاهى خدا را مى‏خوانى ولى در حقیقت توجه و اقبال تو به غیر خداوند است. و چه بسا خود را نصیحت کننده و خدمتگزار مردم قرار مى‏دهى؛ در صورتى که مقصد و منظور تو دعوت مردم به سوى خود است. و گاهى هست که در مقابل مردم خود را کوچک شمرده و مذمت مى‏کنى؛ در صورتى که منظور تو بزرگ کردن و تعریف خود است. و بدان تو نخواهى توانست که از گرفتارى‏ها و تاریکى‏هاى خودبینى و آرزوهاى دراز داشتن بیرون بروى، مگر آنکه از روى صدق و حقیقت به سوى خداوند بزرگ برگشته و در مقابل او اخبات داشته باشی و عیوب و نواقص احوال خود را بشناسى؛ آن عیوب و نواقصى که بر خلاف عقل و علم بوده و با دین و شریعت و سنت رسول اکرم و طریقت حضرات ائمه موافقت نمى‏کند. و هر گاه تو راضى باشى به آن حالاتى که دارى؛ پس کسى نیست که از لحاظ علم و عمل از تو شقى‏تر باشد و نه برای عمرش از تو ضایع‏کننده‏تر باشد و نه در روز قیامت تأثر و حسرت او از تو بیشتر باشد.", 19, 107);
