INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("نسبت به شبهاتی که برای شما پیش می‌آید چکار می‌کنید؟", "امام علی فرمودند: یقینا تقوا در امروز دنيا، براى انسان به منزله يک حصار و به منزله يک سپر است و در فرداى آخرت، راه به سوى بهشت است.", 17, 118);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در زندگی سعی می‌کنید تا نظرات چه کسی را اجرا کنید؟", "امام علی فرمودند:... با تقوا خواب خود را به بيدارى تبدیل کنید و روزتان را با آن سپرى کنيد. دل‏هاى خود را با تقوا زنده کنيد و گناهان خود را با آن شست و شو دهيد. بيمارى‏هاى خود را با تقوا درمان و خود را آماده سفر آخرت گردانيد. از تباه کنندگان تقوا عبرت بگيريد و خودتان، عبرت پرهيزکاران نشويد. آگاه باشيد، تقوا را حفظ کنيد و خويشتن را با تقوا حفظ نماييد.", 17, 119);
