INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در زندگی روی خودتان کنترل دارید؟", "ای فرزندان آدم، ما لباسی که شرمگاهتان را می‌پوشاند و لباسی فاخر و گران که مایه زینت و جمال است، برای شما نازل کردیم و لباس تقوا بهتر است و این از نشانه‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های خداست؛ شاید که متذکّر شوند.", 17, 123);
