INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما به ارتکاب گناه یا ترک طاعت فکر می‌کنید؟", "زراره از یکى از دو امام باقر و یا صادق نقل می‌کند: همانا خداوند تبارک و تعالى براى آدم در فرزندانش مقرر ساخته که هر که قصد کار نیکى کند و آن کار را نکند، براى او یک حسنه نوشته شود و هر که آهنگ کار نیکى کند و انجامش دهد، براى او ده حسنه نوشته شود و هر که آهنگ کار بد کند و آن را نکند بر او نوشته نشود و هر که آهنگ گناه کند و آن را انجام دهد یک گناه بر او نوشته شود.", 21, 125);
