INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در چه شرایطی ارتکاب گناه برای شما آسان و لذت بخش‌تر است؟", "امیرالمومنین فرمودند: بدترین افراد شرور، کسی است که از مردم حیا نمی‌کند و از خداوند سبحان نیز نمی‌ترسد.", 21, 132);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("از ارتکاب گناه و ترک طاعت دیگران رضایت دارید؟", "امام علیفرمودند: آن کسی که به عمل قومى راضى باشد همانند آن است که در آن کار همراه آن قوم بوده و بر هر وارد در باطل دو گناه است؛ گناه انجام باطل، و گناه رضایت به آن‏.", 21, 133);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("نسبت به ترک طاعت و ارتکاب معصیت دیگران چه احساسی دارید؟", "امام علیفرمودند: آن کسی که به عمل قومى راضى باشد همانند آن است که در آن کار همراه آن قوم بوده و بر هر وارد در باطل دو گناه است؛ گناه انجام باطل، و گناه رضایت به آن‏.", 21, 133);
