INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما در ارتکاب معاصی و ترک طاعات، به دیگران کمک‏ می‌کنید؟", "امام علی فرمودند: طلبِ یارى بر کمک نمودن به باطل جنایت و خیانت است.", 21, 134);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("تصور دیگران از شما در ارتکاب آن‌ها به گناه و ترک طاعت چیست؟", "امام باقر فرمودند: هر بنده‏اى از بندگان خدا که سنّت و روش نیکی بگذارد، برای او پاداشى همانند پاداش عمل‏کننده به آن می‌باشد؛ بى‏آنکه از پاداش آن‌ها چیزى کاسته شود و هر بنده‏اى از بندگان خدا که روشى گمراه‏کننده‌ای بنهد، برای او بار گناهى همانند بار گناه عمل‏کننده به آن باشد؛ بى‏آنکه از بار گناه آن‌ها چیزى کاسته شود.", 21, 135);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فکر آلوده کردن دیگران به گناه را دارید؟", "امام باقر فرمودند: هر بنده‏اى از بندگان خدا که سنّت و روش نیکی بگذارد، برای او پاداشى همانند پاداش عمل‏کننده به آن می‌باشد؛ بى‏آنکه از پاداش آن‌ها چیزى کاسته شود و هر بنده‏اى از بندگان خدا که روشى گمراه‏کننده‌ای بنهد، برای او بار گناهى همانند بار گناه عمل‏کننده به آن باشد؛ بى‏آنکه از بار گناه آن‌ها چیزى کاسته شود.", 21, 135);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("نسبت به انجام گناه و ترک طاعت دیگران در جامعه چه اقدامی انجام می‌دهید؟", "امام باقر فرمودند: هر بنده‏اى از بندگان خدا که سنّت و روش نیکی بگذارد، برای او پاداشى همانند پاداش عمل‏کننده به آن می‌باشد؛ بى‏آنکه از پاداش آن‌ها چیزى کاسته شود و هر بنده‏اى از بندگان خدا که روشى گمراه‏کننده‌ای بنهد، برای او بار گناهى همانند بار گناه عمل‏کننده به آن باشد؛ بى‏آنکه از بار گناه آن‌ها چیزى کاسته شود.", 21, 135);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر در ذهن خود به فکر ترک گناه هستید؟", "عبد اللَّه بن موسى بن جعفر از پدرش نقل می‌کند: از او درمورد دو فرشته پرسیدم که آیا هر گاه بنده قصد گناه کند یا قصد کار نیک کند، می‌دانند؟ فرمودند: بوى بد و بوى خوب یکى است؟ گفتم: نه؛ فرمودند: همانا بنده چون قصد کار نیک کند نفَسَش خوشبو بیرون می‌آید؛ پس فرشته سمت راست به فرشته سمت چپ می‌گوید: برخیز [و دنبال کار خود برو] زیرا این بنده قصد کار خوب کرده و هنگامى که آن کار خوب را انجام داد، زبانش قلم او باشد و آب دهانش مرکب او و آن را براى او ثبت کند و هر گاه آهنگ گناه کند، نفسش بدبو بیرون می‌آید؛ پس فرشته سمت چپ به سمت راستى می‌گوید: توقف کن؛ زیرا او آهنگ گناه کرده و چون آن گناه را بجا آورد، زبانش قلم او است و مرکبش آب دهان او و آن گناه را برای او ثبت می‌کند.", 20, 136);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر نیت انجام طاعات الهی را در ذهن خود دارید؟", "عبد اللَّه بن موسى بن جعفر از پدرش نقل می‌کند: از او درمورد دو فرشته پرسیدم که آیا هر گاه بنده قصد گناه کند یا قصد کار نیک کند، می‌دانند؟ فرمودند: بوى بد و بوى خوب یکى است؟ گفتم: نه؛ فرمودند: همانا بنده چون قصد کار نیک کند نفَسَش خوشبو بیرون می‌آید؛ پس فرشته سمت راست به فرشته سمت چپ می‌گوید: برخیز [و دنبال کار خود برو] زیرا این بنده قصد کار خوب کرده و هنگامى که آن کار خوب را انجام داد، زبانش قلم او باشد و آب دهانش مرکب او و آن را براى او ثبت کند و هر گاه آهنگ گناه کند، نفسش بدبو بیرون می‌آید؛ پس فرشته سمت چپ به سمت راستى می‌گوید: توقف کن؛ زیرا او آهنگ گناه کرده و چون آن گناه را بجا آورد، زبانش قلم او است و مرکبش آب دهان او و آن گناه را برای او ثبت می‌کند.", 20, 136);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در انجام طاعت و ترک گناه‏ چقدر موفق می‌شوید؟", "زراره از یکى از دو امام باقر و یا صادق نقل می‌کند: یقیناً خداوند تبارک‏ و تعالى براى آدم در فرزندانش مقرر ساخته که هر که قصد کار نیکى کند و آن کار را نکند براى او یک حسنه نوشته شود و هر که قصد کار نیکى کند و آن را انجام دهد، براى او ده حسنه نوشته شود و هر که آهنگ کار بد کند و آن را نکند برای او نوشته نمی‌شود و هر که قصد آن را بکند و انجام دهد، یک گناه برای او نوشته می‌شود.", 20, 137);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فکر می‌کنید انجام طاعت و ترک گناه شما سر وقت خودش انجام می‌گیرد؟", "زراره از یکى از دو امام باقر و یا صادق نقل می‌کند: یقیناً خداوند تبارک‏ و تعالى براى آدم در فرزندانش مقرر ساخته که هر که قصد کار نیکى کند و آن کار را نکند براى او یک حسنه نوشته شود و هر که قصد کار نیکى کند و آن را انجام دهد، براى او ده حسنه نوشته شود و هر که آهنگ کار بد کند و آن را نکند برای او نوشته نمی‌شود و هر که قصد آن را بکند و انجام دهد، یک گناه برای او نوشته می‌شود.", 20, 137);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در شرایط نامساعد زندگی انجام طاعت و ترک معصیت شما چگونه است؟", "زراره از یکى از دو امام باقر و یا صادق نقل می‌کند: یقیناً خداوند تبارک‏ و تعالى براى آدم در فرزندانش مقرر ساخته که هر که قصد کار نیکى کند و آن کار را نکند براى او یک حسنه نوشته شود و هر که قصد کار نیکى کند و آن را انجام دهد، براى او ده حسنه نوشته شود و هر که آهنگ کار بد کند و آن را نکند برای او نوشته نمی‌شود و هر که قصد آن را بکند و انجام دهد، یک گناه برای او نوشته می‌شود.", 20, 137);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در شرایط مساعد زندگی انجام طاعت و ترک معصیت شما چگونه است؟", "زراره از یکى از دو امام باقر و یا صادق نقل می‌کند: یقیناً خداوند تبارک‏ و تعالى براى آدم در فرزندانش مقرر ساخته که هر که قصد کار نیکى کند و آن کار را نکند براى او یک حسنه نوشته شود و هر که قصد کار نیکى کند و آن را انجام دهد، براى او ده حسنه نوشته شود و هر که آهنگ کار بد کند و آن را نکند برای او نوشته نمی‌شود و هر که قصد آن را بکند و انجام دهد، یک گناه برای او نوشته می‌شود.", 20, 137);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("انجام طاعت و ترک گناه شما، برای چه کسی است؟", "امام باقرفرمودند: میان حق و باطل چیزى جز کمى عقل فاصله نیست. گفتند: چگونه چنین است اى پسر پیامبرخدا، گفت: بنده عملى مى‏کند که خرسندى خدا در آن است؛ اما او براى خدا نمى‏کند؛ در صورتى که اگر عمل خویش را خالص براى خدا انجام مى‏داد، آن جهت دیگر نیز براى او به زودى حاصل مى‏گشت.", 20, 138);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("بعد از انجام طاعت و ترک معصیت، توقع ابراز احساسات از دیگران را دارید؟", "امام باقرفرمودند: میان حق و باطل چیزى جز کمى عقل فاصله نیست. گفتند: چگونه چنین است اى پسر پیامبرخدا، گفت: بنده عملى مى‏کند که خرسندى خدا در آن است؛ اما او براى خدا نمى‏کند؛ در صورتى که اگر عمل خویش را خالص براى خدا انجام مى‏داد، آن جهت دیگر نیز براى او به زودى حاصل مى‏گشت.", 20, 138);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("احساس شما بعد از انجام طاعت و ترک گناه چیست؟", "امام علی فرمودند: گناهى که تو را پشیمان می‌کند، بهتر از کار نیکى است که تو را به خود پسندى وا می‌دارد.", 20, 139);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("خوشحالیِ انجام طاعت و ترک گناه چه حسی را در شما نسبت به دیگران به وجود می‌آورد؟", "امام علی فرمودند: گناهى که تو را پشیمان می‌کند، بهتر از کار نیکى است که تو را به خود پسندى وا می‌دارد.", 20, 139);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("تحمل شما برای انجام دادن طاعات و ترک گناهان چقدر است؟", "حضرت باقر فرمودند: نگهدارى عمل از خود عمل سخت‌تر است؛ راوى عرض کرد: نگهدارى عمل چیست؟ فرمودند: مردى براى خداوند یگانه بى‏شریک بخششى می‌کند و خرجى می‌نماید، برایش عمل پنهانى نوشته مى‏شود؛ سپس کارش را به زبان مى‏آورد و برایش عمل آشکار می‌نویسند، باز هم به زبان مى‏آورد تا این‌که محو می‌شود و برایش ریا مى‏نویسند.", 20, 140);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما طاعات انجام شده و معاصی ترک شده خود را چگونه می‌بینید؟", "امام سجاد فرمودند:... و اندک شمردن نیکی‌هاى خود در کردار و گفتار هر چند بسیار باشد و بسیار شمردن بدی‌هاى خود در کردار و گفتار هر چند اندک باشد... .", 20, 141);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("انجام طاعات و ترک گناهان را تکرار می‌کنید؟", "امام علی فرمودند: کار اندکی که بر آن مداومت کنى بهتر است از کار زیادى که از آن خسته شوى‏.", 20, 142);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما در انجام طاعات و ترک گناهان تداوم دارید؟", "امام علی فرمودند: کار اندکی که بر آن مداومت کنى بهتر است از کار زیادى که از آن خسته شوى‏.", 20, 142);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("طاعاتتان را چگونه انجام می‌دهید؟", "عباس غلام حضرت رضا گوید: شنیدم آن حضرت می‌فرمودند: پنهان‏کردن کار نیک برابر با هفتاد حسنه است و آشکارا کننده کار بد مخذول و خوار است و پنهان‏کننده کار بد آمرزیده است.", 20, 143);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("إحساس شما نسبت به انجام طاعت و ترک گناه دیگران چگونه است؟", "امام علی فرمودند:... اى مردم، جز این نیست که خشنودى و خشم است که مردم را بر یک محور جمع مى‏کند. ناقه ثمود را یقیناً یک نفر پى کرد؛ اما عذاب خداوند همه را گرفت؛ چون همه به آن کار رضایت دادند. خداوند سبحان فرمودند: «همه آن را پى کردند؛ پس همه پشیمان شدند.»[الشعرا، 157] آنگاه سرزمین آن‌ها بر اثر فرو رفتن صدا کرد، مانند صداى فرو رفتن آهن داغ در زمین نرم و هموار. اى مردم، هر که راه راست و روشن را بپیماید به آب برسد و هر که بیراهه رود در بیابان به سرگردانى افتد... .", 20, 144);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("کمک‌رسانی شما به دیگران در انجام طاعات و ترک معاصی چگونه است؟", "ای کسانی که ایمان آورده‌اید، شعائر و حدود الهی و ماه حرام و قربانی‌های بی‌نشان و نشاندار و آن‌ها را که به قصد خانه خدا برای به دست آوردن فضل پروردگار و خشنودی او می‌آیند را حلال ندانید؛ اما هنگامی که از احرام بیرون آمدید، صیدکردن برای شما منعی ندارد و خصومت با جمعیّتی که شما را از آمدن به مسجد الحرام بازداشتند، نباید شما را وادار به تعدّی و تجاوز کند و در راه نیکی و پرهیزکاری با هم تعاون کنید و در راه گناه و تعدّی همکاری نکنید و از خدا بپرهیزید که مجازات خدا شدید است.(مائده، 2)", 20, 145);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما نسبت به انجام طاعت و ترک معصیت در اجتماع چه وظیفه‌ای دارید؟", "سماعه گوید: از امام صادق این قول خداوند عز و جل را پرسیدم:«هر که انسانى را بجز براى قصاص انسانى بکشد، گویا همه مردم را کشته و هر که او را زنده کند گویا همه مردم را زنده کرده» [مائده، 32] فرمودند: هر که او را از گمراهى بسوى هدایت راهنمایی کند، گویا او را زنده کرده‏ و هر که او را از هدایت بسوى گمراهى ببرد، گویا او را کشته است.", 20, 146);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما به تناسخ اعتقاد دارید؟", "امام رضا فرمودند: کسى که معتقد به تناسخ باشد، به خداوند بزرگ کفر ورزیده و بهشت و دوزخ را تکذیب کرده است.", 16, 147);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("برای شناخت بهتر خداوند از تشبیه او به خلقش بهره می‌گیرید؟", "امام رضا فرمودند: هر که خداوند تعالى را به مخلوقاتش تشبیه کند، مشرک است و هر که به خداوند چیزى را نسبت دهد که از آن نهى فرموده است، کافر باشد.", 16, 148);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("برای معرفی و شناساندن خدا به دیگران از تشبیه به خلقش استفاده می‌کنید؟", "امام رضا فرمودند: هر که خداوند تعالى را به مخلوقاتش تشبیه کند، مشرک است و هر که به خداوند چیزى را نسبت دهد که از آن نهى فرموده است، کافر باشد.", 16, 148);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما معتقدید که افراد و جوامع در یک برنامه اجباری از پیش تعیین شده زندگی می‌کنند یا اختیار دارند؟", "امام رضا فرمودند: معتقدِ به جبر کافر و معتقدِ به تفویض مشرک است.", 16, 149);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما فکر می‌کنید افراد و جوامع در زندگی رها شده‌اند و هر کاری می‌توانند انجام دهند یا اختیار دارند؟", "امام رضا فرمودند: معتقدِ به جبر کافر و معتقدِ به تفویض مشرک است.", 16, 149);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چه اعتقادی نسبت به خداوند و پیامبران او دارید؟", "امام صادق فرمودند: کسى که درباره خدا و رسول او شک کند، کافر است.", 16, 150);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("احساس شما نسبت به خانواده آخرین پیامبر چگونه است؟", "امام باقر فرمودند: دوست داشتن ما ایمان است و دشمن داشتن ما کفر.", 16, 151);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("نظرتان نسبت به امامت و پیشوایی چیست؟", "امام صادق فرمودند: کسى که ادّعاى امامت کند در حالى که اهلیت آن را ندارد، کافر است.", 16, 152);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("اعتقاد شما نسبت به منجی آخر الزمان چیست؟", "امام باقر فرمودند: از امور حتمى و تغییر ناپذیر نزد خداوند قیام قائم ماست. هر که در گفته من شک کند، خدا را با حال کفر و انکار او دیدار کند.", 16, 153);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما وقتی در برابر فرهنگ غربی و بیگانه قرار می‌گیرید، چه احساسی دارید؟", "امام رضا فرمودند:... خداوند تعرّب بعد از هجرت را حرام فرموده؛ براى این‌که آن بازگشت از دین است به بى‏دینى و یارى ننمودن پیامبران و حجّت‌هاى خدا و آنچه از فساد در آن است و نابود کردن حق هر ذى حقّى، نه تنها به دلیل بیابان‏نشینى، چراکه اگر با حقّ و حقوق هم آشنا شد و دین را کاملا دریافت و شناخت، اجازه ندارد با مردم عوام و بى‏فرهنگ و جاهل آمیزش داشته باشد و این کار براى او خطرناک است؛ زیرا اطمینان نیست که کم کم کارش به آنجا برسد که آنچه می‌داند زیر پا گذاشته و مانند مردمان جاهل رفتار کند و افراط هم بورزد... .", 16, 154);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("هنگامی که با فرهنگ غرب مواجه می‌شوید، چه واکنشی نسبت به آن دارید؟", "امام رضا فرمودند:... خداوند تعرّب بعد از هجرت را حرام فرموده؛ براى این‌که آن بازگشت از دین است به بى‏دینى و یارى ننمودن پیامبران و حجّت‌هاى خدا و آنچه از فساد در آن است و نابود کردن حق هر ذى حقّى، نه تنها به دلیل بیابان‏نشینى، چراکه اگر با حقّ و حقوق هم آشنا شد و دین را کاملا دریافت و شناخت، اجازه ندارد با مردم عوام و بى‏فرهنگ و جاهل آمیزش داشته باشد و این کار براى او خطرناک است؛ زیرا اطمینان نیست که کم کم کارش به آنجا برسد که آنچه می‌داند زیر پا گذاشته و مانند مردمان جاهل رفتار کند و افراط هم بورزد... .", 16, 154);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شک در کجای اقدامات شما با آن‌ها آمیخته می‌شود؟", "امام على فرمودند: هر که نسبت به چیزى یقین داشته باشد و سپس دچار تردید شود، بر یقین خود بماند؛ زیرا یقین با شک از بین نمى‏رود.", 12, 155);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر از تصمیماتی که می‌گیرید با توجه به نتایج آن است؟", "امام على فرمودند: هر که از دیدن آنچه پیشِ روى اوست، کور باشد، [نهال‏] شک را در سینه خود مى‏نشانَد.", 12, 156);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فکر می‌کنید فرد آینده نگری هستید؟", "امام على فرمودند: هر که از دیدن آنچه پیشِ روى اوست، کور باشد، [نهال‏] شک را در سینه خود مى‏نشانَد.", 12, 156);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("غالبا وقتی کاری را شروع می‌کنید شک و تردید در شما نسبت به آن کار، روز به روز بیشتر می‌شود؟", "امام باقر درباره کلام خداوند متعال «اما کسانى که در دل‌هایشان بیمارى است پس پلیدى بر پلیدیشان اضافه نمود.»[التوبه، 125] فرمودند: یعنى شکى بر شک آن‌ها [بیفزود].", 12, 157);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("تصمیمات و اقدامات شما در زندگی، چقدر عقلانی است؟", "امام على فرمودند: آیا نمى‏بینید که خداوند سبحان همه انسان‌ها را، از آدم، تا آخرین نفرِ این عالم، با سنگ‌هایى بیازمود که نه زیانى مى‏رسانند و نه سودى... اگر پایه‏هایى که خانه (کعبه) بر آن بنا شده و سنگ‌هایى که با آن‌ها برافراشته گردیده است، از زمرّد سبز و یاقوت سرخ و نور و درخشندگى بود، یقینا از جولان شک در سینه‏ها مى‏کاست و کوشش ابلیس را از دل‌ها برمى‏داشت و از انباشت تردید و به تلاطم در آمدن ریب در مردم جلوگیرى مى‏کرد؛ اما خداوند بندگان خود را با سختی‌هاى گوناگون مى‏آزماید.", 12, 158);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("وقتی شرایط زندگی تغییر می‌کند شک در شما چه وضعیتی پیدا می‌کند؟", "امام على فرمودند:... سپس خداوند آدم را در خانه‏اى مسکن داد که زندگى در آن گوارا بود. جایگاه او را امن و امان بخشید و او را از شیطان و دشمنى او ترساند. پس شیطان او را فریب داد؛ بدان علّت که از زندگى آدم در بهشت و همنشینى او با نیکان حسادت ورزید. پس آدم یقین را به تردید و عزم استوار را به گفته‏هاى ناپایدار شیطان فروخت... .", 12, 159);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("خیلی زود به چیزهایی که می‌شنوید یا می‌بینید باور پیدا می‌کنید؟", "امام على فرمودند: شک، ثمره نادانى است.", 12, 160);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("به چه چیزهایی در زندگی باور پیدا می‌کنید؟", "امام على فرمودند: داناترین مردم، کسى است که شک، یقین او را از بین نبرد.", 12, 160);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("افزایش و گسترش گناهان در زندگی چه حسی را در شما به وجود می‌آورد؟", "امام على فرمودند:... هرکه درباره خدا گردنکشى کند، دستخوش شک شود و هرکه شک کند، خداوند بر او چیره گردد و با قدرت خود وى را به خوارى افکند و با جلال خود وى را حقیر گرداند، همانطور که او به پروردگار بزرگوار خود مغرور شد و در کار خداوند کوتاهى کرد.", 12, 165);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("لحظه به لحظه در روند پیشبرد زندگی یقین یا شکتان افزایش می‌یابد؟", "امام على فرمودند: ثمره شک، سرگردانى است.", 12, 161);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر تفکر در اقدام به انجام یا ترک شما نقش دارد؟", "امام على فرمودند: با اندیشیدن پیاپى، شک ناپدید مى‏شود.", 12, 162);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر در انجام‌ها و ترک‌هایتان در زندگی توجه به اخلاص و یقین دارید؟", "امام على فرمودند: هیچ مخلصى دچار شبهه نشد و هیچ یقین‏مندى شک نکرد.", 12, 163);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("اطرافیان، شما را فردی سپاس‌گزار می‌دانند یا ناسپاس؟", "ابو اسحاق خراسانى گوید: امیرالمؤمنین در خطبه‏اش می‌فرمودند: تردید به خود راه ندهید که به شک می‌افتید و در نهایت کافر می‌شوید.", 12, 164);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در مواجه با اشتباهاتتان در زندگی چگونه رفتار می‌کنید؟", "برید عجلى گوید: از امام باقر از کمترین چیزى که بنده به آن مشرک گردد پرسیدم ؟ فرمودند: هر که به هسته‏اى گوید: سنگریزه و یا به سنگریزه‏اى گوید: هسته است و سپس به آن متدین گردد.", 13, 166);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("دوستی‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌ها و دشمنی‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌هایتان بر چه اساسی است؟", "ابى العباس گوید: از امام صادق از کمترین چیزى که انسان به آن مشرک گردد؛ پرسیدم . فرمودند: کسى که عقیده‏اى را بدعت گذارد و به خاطر آن حبّ و بغض ورزد.", 13, 167);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر در زندگی خود را ملزم به اجرای دستورات الهی می‌دانید؟", "امام صادق در تفسیر قول خداوند عز و جل «و بیشتر آن‌ها به خدا ایمان ندارند جز این‌که آن‌ها مشرکند.» فرمودند: [هرکس] شیطان را ندانسته فرمان برد، مشرک شود.", 13, 168);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در مواجهه با عالم گمراهی که حکم و برنامه حرام می‌دهد، چگونه رفتار می‌کنید؟", "ابى بصیر گوید: از امام صادق از قول خدا عز و جل «دانشمندان و عابدان خود را در برابر خدا، ارباب و پروردگار خود گرفتند» پرسیدم: در پاسخ فرمودند: اما به خدا آن‌ها را به پرستش خودشان دعوت نکردند و اگر هم دعوت مى‏کردند آن‌ها نمى‏پذیرفتند ولى طبق نظر خودشان حرام خدا را براى آن‌ها حلال کردند و حلال خدا را براى آن‌ها حرام کردند؛ در نتیجه آن‌ها را ندانسته و نفهمیده اطاعت و پرستش کردند.", 13, 169);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("اگر بزرگتری یا شخصی که نزد شما جایگاهی دارد، شما را به ارتکاب گناه دستور دهد اطاعت می‌کنید؟", "امام صادق فرمودند: هر کس مردى را در نافرمانى خدا فرمان برد، محققاً او را پرستیده است.", 13, 170);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("دوست دارید وقتی کار نیکی انجام می‌دهید دیگران آن را ببیند؟", "امام صادق در باره قول خداوند عز و جل «هر کس به لقاء پروردگارش امید دارد، باید عملى صالح کند و در عبادت پروردگارش هیچ کس را شریک نسازد.» [الکهف، 110] فرمودند: شخصى کار ثوابى می‌کند و مقصودش خدا نیست، بلکه می‌خواهد مردم بشنوند و او را بستایند؛ این شخص کسی است که در عبادت پروردگارش شریک مى‏آورد، سپس فرمودند: هرگز چنین نباشد که بنده‌ای در نهان کار خیرى کند و روزگار بگذرد، جز آنکه خدا برایش خیرى ظاهر سازد و هرگز بنده‌ای نباشد که در نهان شرى کند و روزگار بگذرد، جز آنکه خدا برایش شرى آشکار کند.", 13, 171);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("دوست دارید کارهای نیکی که انجام دادید را دیگران بشنوند؟", "امام صادق در باره قول خداوند عز و جل «هر کس به لقاء پروردگارش امید دارد، باید عملى صالح کند و در عبادت پروردگارش هیچ کس را شریک نسازد.» [الکهف، 110] فرمودند: شخصى کار ثوابى می‌کند و مقصودش خدا نیست، بلکه می‌خواهد مردم بشنوند و او را بستایند؛ این شخص کسی است که در عبادت پروردگارش شریک مى‏آورد، سپس فرمودند: هرگز چنین نباشد که بنده‌ای در نهان کار خیرى کند و روزگار بگذرد، جز آنکه خدا برایش خیرى ظاهر سازد و هرگز بنده‌ای نباشد که در نهان شرى کند و روزگار بگذرد، جز آنکه خدا برایش شرى آشکار کند.", 13, 171);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر خداوند را در تصمیمات و اقدامات زندگی خود موثر می‌دانید؟", "حضرت صادق در تفسیر آیه «ایمان اکثر این مردم آلوده به شرک است»[يوسف، 106] فرمودند: مقصود گفتارى است که انسان گاهى مثلا می‌گوید: اگر فلانى نبود من نابود می‌شدم. اگر فلان کس نبود من به فلان ناراحتى مبتلا مى‏شدم. اگر فلانى نبود خانواده من از بین می‌رفتند. مگر مشاهده نمى‏کنى که در همه این موارد براى خداوند شریک قائل شده است. او دیگرى را رازق و دافع دانسته است. راوى گوید: عرض کردم اگر بگویم: خداوند بر من منت نهاد و فلان شخص را رسانید و او مرا نجات داد. فرمودند: این گونه مانعى ندارد.", 13, 172);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما گمان می‌کنید که خداوند طبیعت را در زندگی شما موثر قرار داده است؟", "یعقوب بن شعیب گفت از امام ششم در مورد قول خداوند متعال‏ «و بیشتر آن‌ها ایمان نمى‏آورند مگر آن که مشرک باشند»[يوسف، 106] پرسیدم. فرمودند: آن‌ها مى‏گفتند: با [غروب‏] فلان ستاره و [طلوع‏] بهمان ستاره، باران مى‏بارد یا به پیشگوها مراجعه مى‏کردند و گفته‏هاى آن‌ها را باور مى‏داشتند [و همه اینها شرک خفى است‏].", 13, 172);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("برای دست‌یابی به اهدافتان، از پرداخت رشوه استفاده می‌کنید؟", "رسول خدا فرمودند: از رشوه بپرهیزید؛ چراکه کفر محض است و صاحب رشوه، بوى بهشت را استشمام نمى‏کند.", 14, 173);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در ارتباط با دیگران چه رفتاری دارید؟", "امام صادق در پاسخ به سؤال از کمترین درجه الحاد فرمودند: کبر از آن [جمله‏] است.", 14, 174);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در بین نظرات و ایده‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های شخصی و دستورات الهی کدامشان برای شما اولویت دارد؟", "امام على در پاسخ به کسى که پرسید: کمترین چیزى که آدمى با آن کافر مى‏شود چیست؟ فرمودند: کمترین چیزى که [انسان‏] به سبب آن کافر مى‏گردد، این است که به چیزى اعتقاد دینى پیدا کند و آنچه را که خدا از آن نهى کرده، خیال کند که خداوند او را بدان فرمان داده است؛ سپس با عَلَم کردن آن، دوستى و دشمنى ورزد و خیال کند که با این کار خدا را عبادت مى‏کند.", 14, 175);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("ملاک محبت و تنفرورزی شما با افراد در خوشایندی و ناخوشایندی چیست؟", "امام على در پاسخ به کسى که پرسید: کمترین چیزى که آدمى با آن کافر مى‏شود چیست؟ فرمودند: کمترین چیزى که [انسان‏] به سبب آن کافر مى‏گردد، این است که به چیزى اعتقاد دینى پیدا کند و آنچه را که خدا از آن نهى کرده، خیال کند که خداوند او را بدان فرمان داده است؛ سپس با عَلَم کردن آن، دوستى و دشمنى ورزد و خیال کند که با این کار خدا را عبادت مى‏کند.", 14, 175);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("افراد و حکومت‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های مورد پسند شما در طول تاریخ چه کسانی هستند؟", "و کسانى که کفر ورزیده‌اند، سرورانشان طاغوتند که آن‌ها را از روشنایى به سوى تاریکی‌ها خارج می‌کنند.(بقره 257)", 14, 176);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در حال حاضر از کدام افراد و حکومت‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های حمایت می‌کنید؟", "و کسانى که کفر ورزیده‌اند، سرورانشان طاغوتند که آن‌ها را از روشنایى به سوى تاریکی‌ها خارج می‌کنند.(بقره 257)", 14, 176);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("به خداوند اعتقاد دارید؟", "و هر که به خدا و فرشتگان او و کتاب‌ها و فرستادگانش و روز واپسین کافر شود، قطعا سخت گمراه شده است.(نساء 136)", 14, 177);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرشتگان خداوند را قبول دارید؟", "و هر که به خدا و فرشتگان او و کتاب‌ها و فرستادگانش و روز واپسین کافر شود، قطعا سخت گمراه شده است.(نساء 136)", 14, 177);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("معتقد به فرستادگان خداوند هستید؟", "و هر که به خدا و فرشتگان او و کتاب‌ها و فرستادگانش و روز واپسین کافر شود، قطعا سخت گمراه شده است.(نساء 136)", 14, 177);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("کتاب‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های آسمانی خداوند را قبول دارید؟", "و هر که به خدا و فرشتگان او و کتاب‌ها و فرستادگانش و روز واپسین کافر شود، قطعا سخت گمراه شده است.(نساء 136)", 14, 177);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما کافر نیستید و کافر بودن کفار را قبول دارید؟", "و هر که به خدا و فرشتگان او و کتاب‌ها و فرستادگانش و روز واپسین کافر شود، قطعا سخت گمراه شده است.(نساء 136)", 14, 177);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("کفر در اعمال شما ظهور و بروز دارد؟", "از محمّد بن مسلم نقل است که نزد امام صادق بودم که ابوبصیر نزد ایشان آمد و پرسید: اى ابا عبداللَّه، درباره کسى که در خدا شک کند، چه مى‏فرمایى؟ امام فرمودند: اى ابامحمّد، او کافر است. پرسید: اگر درباره رسول خدا شک کند؟ فرمودند: کافر است. راوى گوید: حضرت سپس رو به زراره کرد و فرمودند: در حقیقت وقتى کافر مى‏شود که انکار کند.", 14, 177);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چیزهایی که به آن‌ها مشغولیت دارید، شما را به یقین می‌رساند یا انکار؟", "امام باقر فرمودند: هر چیزى که از اقرار [به خدا] و تسلیم [شدن به اوامر او] برخیزد، آن ایمان است و هر چیزى که از انکار و نفى [حق] مایه بگیرد، آن کفر است.", 14, 178);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("اقدامات و تصمیمات شما در زندگی بر چه اساسی است؟", "امام باقر فرمودند: هر چیزى که از اقرار [به خدا] و تسلیم [شدن به اوامر او] برخیزد، آن ایمان است و هر چیزى که از انکار و نفى [حق] مایه بگیرد، آن کفر است.", 14, 178);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در مواجهه با گناهان خود یا دیگران چگونه رفتار می‌کنید؟", "امام صادق فرمودند: معناى کفر، هر معصیتى است که با آن، از روى نفى و انکار و استخفاف و بى‏اعتنایى به ریز و درشت، خداوند نافرمانى شود و عمل کننده به آن، کافر است... چنین کسى اگر به سبب هواى نفْسِ خویش به معصیتى گرایش پیدا کند و انگیزه‏اش انکار و استخفاف و بى‏اعتنایى باشد، کافر شده است و اگر به سبب هواى نفْس خویش به دیندارى روى آورد و انگیزه‏اش تأویل و تقلید و تسلیم و تن دادن به گفته‏هاى پدران و نیاکانش باشد، شرک ورزیده است.", 14, 178);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چگونه واجبات الهی را اجرا می‌کنید؟", "امام صادق فرمودند: خداوند عزّوجلّ فرایضى را بر بندگان واجب ساخته است؛ پس هر که فریضه‏اى واجب را فروگذارد و به آن عمل نکند و منکرش شود، او کافر است.", 14, 179);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما در زندگی آلوده به ظلم و ستم می‌شوید؟", "اى کسانى که ایمان آورده‏اید، از آنچه به شما روزى‏ داده‏ایم انفاق کنید؛ پیش از آن که روزى فرا رسد که در آن نه داد و ستدى است و نه دوستى و نه شفاعتى و کافران همان ستمکارانند .(بقره، 254)", 14, 180);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("لذت‌های زودگذر چه جایگاهی در زندگی شما دارند؟", "خداوند کسانى را که ایمان آورده و کارهاى شایسته کرده‌اند، در باغ‌هایى که از زیر [درختان‏] آن‌ها نهرها روان است، درمى‏آورد و [حال آن که‏] کسانى که کافر شده‌اند [در ظاهر] بهره مى‏برند و مانند چهارپایان مى‏خورند و آتش جایگاه آن‌ها است.(محمد، 12)", 14, 181);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("حدودا هر چند وقت یک‌بار در دنیا سختی و گرفتاری برای شما پیش می‌آید؟", "امام على فرمودند: بهشت کافر، دنیا ‏و همّتش جهان‏گذرا و بدبختى‏اش مرگ و خط پایانش آتش است.", 14, 182);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("از کی احساس راحتی و فراخی در دنیا به شما دست داده است؟", "ابراهیم بن عبد الحمید مى‏گوید: امام موسى بن جعفر از امام صادق نقل مى‏کنند که فرمودند: دنیا زندان مؤمن و قبر حصار او و بهشت جایگاه او است و دنیا بهشت کافر و قبر زندان او و آتش جایگاه اوست‏.", 14, 182);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در ارتباط با دیگران چه نوع رفتاری دارید؟", "امام على فرمودند: کافر نیرنگ باز و حیله‏گر و بیرحم و خیانت کار است.", 14, 183);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در ارتباط با دیگران از خشونت و رفتارهای سنگدلانه استفاده می‌کنید؟", "امام على فرمودند: کافر نیرنگ باز و حیله‏گر و بیرحم و خیانت کار است.", 14, 183);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر به اعتمادی که دیگران به شما کرده‌اند خیانت می‌کنید؟", "امام على فرمودند: کافر نیرنگ باز و حیله‏گر و بیرحم و خیانت کار است.", 14, 183);
