INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("تمام هم و غم شما برای دست‌یابی به چه چیزی است؟", "امام على فرمودند: همّ و غم کافر براى دنیاى اوست و کوشش او براى جهان گذرایش و مقصدش شهوتش مى‏باشد.", 14, 185);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("تاثیر فهم شما از دین بر اخلاقتان چگونه است؟", "محمّد بن جعفر از پدرش امام صادق از پدران خود روایت نموده که رسول خدا فرمود: دو خوى پسندیده در منافق جمع نشود؛ فهم در دین اسلام و اثر خوشرویى در چهره.", 15, 186);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("تاثیر اخلاق بر فهم دین شما چگونه است؟", "محمّد بن جعفر از پدرش امام صادق از پدران خود روایت نموده که رسول خدا فرمود: دو خوى پسندیده در منافق جمع نشود؛ فهم در دین اسلام و اثر خوشرویى در چهره.", 15, 186);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("وقتی در موضوعی با دیگران گفتگو می‌کنید، چگونه در گفتگو مشارکت می‌کنید؟", "محمّد بن جعفر از پدرش امام صادق از پدران خود روایت نموده که رسول خدا فرمود: دو خوى پسندیده در منافق جمع نشود؛ فهم در دین اسلام و اثر خوشرویى در چهره.", 15, 186);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("در هنگام صحبت کردن چقدر مبتلا به غیبت می‌شوید؟", "پیامبر خدا فرمودند: هر که برادر مؤمن خود را پیش رویش بستاید و پشت سرش از او بدگویى کند، پیوند حرمت میان آن‌ها بریده شود.", 15, 187);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("نسبت به داشته‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌های دیگران و جایگاه آن‌ها چه حسی دارید؟", "امام پنجم فرمودند: بد بنده‏اى است، کسی که دو رو و دو زبان داشته باشد و در حضور برادر مسلمان خود، در مدح و ستایش او راه مبالغه را در پیش بگیرد و در دوستى و علاقه به او تظاهر نماید و در غیابش به مذمّت و غیبت او بپردازد و اگر مالى به او برسد، حسادت ببرد و اگر به مشکلى دچار شود، او را تنها بگذارد.", 15, 187);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("هنگام گرفتاری دیگران، چقدر به آن‌ها کمک می‌کنید؟", "امام پنجم فرمودند: بد بنده‏اى است، کسی که دو رو و دو زبان داشته باشد و در حضور برادر مسلمان خود، در مدح و ستایش او راه مبالغه را در پیش بگیرد و در دوستى و علاقه به او تظاهر نماید و در غیابش به مذمّت و غیبت او بپردازد و اگر مالى به او برسد، حسادت ببرد و اگر به مشکلى دچار شود، او را تنها بگذارد.", 15, 187);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("امر به معروف و نهی از منکر دیگران را نسبت به خود می‌پذیرید؟", "امام زین العابدین فرمودند: منافق نهى مى‏کند و خود نهى نمى‏پذیرد و به آنچه دستور مى‏دهد خودش عمل نمى‏کند، چون به نماز ایستد اعتراض می‌کند. گفتم ای پسر پیامبر خدا اعتراض به چه معناست؟ فرمودند: به چپ و راست مى‏نگرد، چون رکوع کند خود را به زمین اندازد، چون سجده کند [مانند پرندگان‏] منقار به زمین زند و چون بنشیند نیم‏خیز نشیند، چون شب شود با این‌که روزه نداشته است همه‏اش به فکر خوردن غذاست و در روز با آنکه شب زنده دارى نکرده است، همّ و غمش خفتن است. اگر سخنى به تو گوید، دروغ گوید و اگر به تو وعده‏اى دهد، خلف وعده کند و اگر به او اعتماد کنى و امانتى بسپارى به تو خیانت کند، اگر از او پنهان باشى تو را غیبت کند (و بدگوئى کند) و اگر به تو وعده بدهد وفا نکند.", 15, 188);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("به امر و نهی‌‌‌‌‌‌‌‌‌‌‌‌‌‌‌هایی که به دیگران می‌کنید، عمل می‌کنید؟", "امام زین العابدین فرمودند: منافق نهى مى‏کند و خود نهى نمى‏پذیرد و به آنچه دستور مى‏دهد خودش عمل نمى‏کند، چون به نماز ایستد اعتراض می‌کند. گفتم ای پسر پیامبر خدا اعتراض به چه معناست؟ فرمودند: به چپ و راست مى‏نگرد، چون رکوع کند خود را به زمین اندازد، چون سجده کند [مانند پرندگان‏] منقار به زمین زند و چون بنشیند نیم‏خیز نشیند، چون شب شود با این‌که روزه نداشته است همه‏اش به فکر خوردن غذاست و در روز با آنکه شب زنده دارى نکرده است، همّ و غمش خفتن است. اگر سخنى به تو گوید، دروغ گوید و اگر به تو وعده‏اى دهد، خلف وعده کند و اگر به او اعتماد کنى و امانتى بسپارى به تو خیانت کند، اگر از او پنهان باشى تو را غیبت کند (و بدگوئى کند) و اگر به تو وعده بدهد وفا نکند.", 15, 188);
