INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("وقتی سخن از افراد خوب می‌شود چه احساسی دارید؟", "امام على فرمودند: اگر با این شمشیرم بینی مومن را قطع کنم تا با من دشمنى ورزد، هرگز دشمنى نخواهد کرد و اگر همه دنیا را به پاى منافق بریزم تا مرا دوست داشته باشد، هیچ‏گاه مرا دوست نخواهد داشت و این از آن روست که قلم قضا بر این امر رقم خورده و این قضاى الهى بر زبان پیامبر امّى گذشته است که فرمودند: اى على، هیچ مؤمنى با تو دشمنى نمى‏کند و هیچ منافقى تو را دوست نمى‏دارد.", 15, 205);
