INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما چقدر با دشمنان و مخالفانتان خوش‌کلام و گشاده‌رو هستید؟", "امام باقر‏ فرمود: هر که با موافقان خود گفتارِ خوش داشته باشد تا با آن‌ها انس بگیرد و با مخالفان خود خوش‌رو باشد تا خود و برادرانش از آن‌ها در امان باشند، از خيرات و درجات بلند نزد خدا چیزهایی به دست آورد که کسی جز خدا اندازه‏اش را نداند.", 23, 220);
