INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر دیگران به دلیل ناسازگاری با شما عیوبتان را برملا کرده‌اند؟", "رسول خدا فرمود: ای ابوذر، هر که در قيامت بدون این سه چيز بيايد زيانکار است. پرسیدم: آن سه چیز چه هستند؟ فرمود: ورع و پارسايى که او را از گناهان حفظ کند، و حلمى که در برابر نادانی سفيهان او را نگه دارد و اخلاقى که بوسيله آن با مردم مدارا کند.", 23, 222);
