INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر به عواقب ناگوار رفتارهای خود نسبت به دیگران توجه دارید؟", "امير مؤمنان علی‏ فرمود: ما نسبت به گروهی خوش¬رویی می‌کنيم در حالی که دل‌هایمان بدخواه آن‌ها است. آنان دشمنان خدا هستند و از آن‌ها تقيّه می‌کنيم، به خاطر برادرانمان، نه براى خودمان.", 23, 223);
