INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("هنگامی که خانه را جارو می‌کنید، چه احساسی دارید؟", "از سلمان فارسى روايت شده: حضرت فاطمه نشسته بود و با آسيايى كه در مقابلش بود جو آسياب مى‏كرد و دسته آسياب خونی بود و در اين حال حسين در گوشه اطاق از گرسنگى بى‏قرارى مى‏كرد. من به فاطمه عرض کردم: اى دختر رسول خدا، پوست دستان شما خراش برداشته است در صورتى كه خادمه شما فضّه حاضر و بی‌كار است. فاطمه فرمود: رسول خدا به من توصيه كرده است كه كار منزل يک روز به عهده من باشد و يک روز به عهده او، و نوبت كار او ديروز بوده است... .", 4, 236);
