INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("نسبت به سایر مومنان و افراد خوب چه احساسی دارید؟", "امام باقر فرمودند: مؤمنان برادر و از یک پدر و مادرند؛ چونکه خداوند سرشت و طینت مؤمنان را از سرشت بهشت آفریده و از باد بهشت بر شکل‏هاى آن‌ها دمیده است؛ پس به همین جهت ایشان با هم برادر و از یک پدر و مادرند.", 9, 269);
