INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("نظرتان نسبت به دنیا برای زندگی چیست؟", "از محمّد بن عجلان [روایت شده که] گفت: نزد امام صادق بودم که شخصى نزد حضرت از فقر و تنگدستى شکایت کرد. حضرت فرمودند: صبر و تحمّل داشته باش که به زودی خداوند برایت گشایشى قرار خواهد داد و پس از لحظه‏اى سکوت به آن شخص فرمودند: از وضعیت زندان شهر کوفه اطلاعى دارى؟ اظهار داشت، خداوند امور شما را اصلاح نماید؛ زندانى تنگ و بدبو مى‏باشد که اهل آن با بدترین حالت به سر مى‏برند. امام فرمودند: همانا تو در یک چنین زندانى هستى؛ آیا مى‏خواهى که در گشایش و رفاه باشى؟ آیا نمى‏دانى که دنیا براى مؤمن همچون زندانى است؟", 9, 270);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("نسبت به امور دنیوی علاقه نشان‏ می‌دهید؟", "از محمّد بن عجلان [روایت شده که] گفت: نزد امام صادق بودم که شخصى نزد حضرت از فقر و تنگدستى شکایت کرد. حضرت فرمودند: صبر و تحمّل داشته باش که به زودی خداوند برایت گشایشى قرار خواهد داد و پس از لحظه‏اى سکوت به آن شخص فرمودند: از وضعیت زندان شهر کوفه اطلاعى دارى؟ اظهار داشت، خداوند امور شما را اصلاح نماید؛ زندانى تنگ و بدبو مى‏باشد که اهل آن با بدترین حالت به سر مى‏برند. امام فرمودند: همانا تو در یک چنین زندانى هستى؛ آیا مى‏خواهى که در گشایش و رفاه باشى؟ آیا نمى‏دانى که دنیا براى مؤمن همچون زندانى است؟", 9, 270);
