INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("ایمان در رفتار و اعمالتان نمایان است؟", "ابو الصّلت هروى از حضرت رضا از پدران بزرگوارش از امیرالمؤمنین علىّ بن ابى طالب از رسول خدا روایت نمود که آن حضرت فرمودند: ایمان عبارت است از گفتارى که بر زبان رانده شود و عملى که عمل شود و شناخت عقل‌ها. ابو الصّلت گوید: این حدیث را در مجلس احمد بن حنبل بازگو نمودم، احمد به من گفت: اى ابا الصّلت اگر این سند [که حاوى این نام‌هاى گرامى است] بر دیوانگان خوانده شود، همانا به‌هوش و سر عقل آیند.", 9, 271);
