INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("دارایی‌های خود را طبق دستورات چه کسانی به کار می‌گیرید؟", "یقیناً خدا از مؤمنان جان‌ها و اموالشان را به بهای این‌که بهشت برای آن‌ها باشد خریده؛ همان کسانی که در راه خدا پیکار می‌کنند، پس [دشمن را] می‌کشند و [خود در راه خدا] کشته می‌شوند. [خدا آن‌ها را] در تورات و انجیل و قرآن وعده‌ای حق داده است و چه کسی به عهد و پیمانش از خدا وفادارتر است؛ پس [ای مؤمنان،] به این داد و ستدی که انجام داده‌اید، خوشحال و شاد باشید و این کامیابی بزرگ است.(توبه، 111)", 9, 275);
