INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("با کفار رابطه صمیمی دارید؟", "ای اهل ایمان، کافران را به جای مؤمنان، سرپرست و یار خود نگیرید. آیا می‌خواهید برای خدا بر ضد خودتان دلیلی آشکار قرار دهید.", 9, 318);
