INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("حدود الهی را در زندگی رعایت کنید؟", "[مؤمنان، همان] توبه کنندگان، عبادت کنندگان، سپاس گزاران، روزه داران، رکوع کنندگان، سجده کنندگان، فرمان دهندگان به معروف و بازدارندگان از منکر و پاسداران حدود و مقرّرات خدا هستند و مؤمنان را [به رحمت و رضوان خدا] مژده بده.(تبه، 112)", 9, 321);
