INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("وقتی یاد خدا‏ می‌افتید چه احساسی دارید؟", "مؤمنان، فقط کسانی هستند که چون یاد خدا شود، قلوبشان ترسان می‌شود و هنگامی که آیات او بر آن‌ها خوانده شود بر ایمانشان می‌افزاید و بر پروردگارشان توکل می‌کنند.(انفال، 2)", 9, 323);
