INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("دیگران را به انجام کدام معروف‌ها امر می‌کنید؟", "مردان و زنان با ایمان دوست و یار یک‌دیگرند؛ همواره به کارهای نیک و شایسته فرمان می‌دهند و از کارهای زشت و ناپسند باز می‌دارند و نماز را برپا می‌دارند و زکات می‌پردازند و از خدا و پیامبرش اطاعت می‌نمایند؛ یقیناً خدا آن‌ها را مورد رحمت قرار می‌دهد؛ زیرا خدا توانای شکست ناپذیر و حکیم است.(توبه، 71)", 9, 324);
