INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("شما تا کنون به مهاجرین پناه داده‌اید؟", "و کسانی که ایمان آوردند و هجرت کردند و در راه خدا جهاد نمودند و آن‌ها که مهاجران را پناه دادند و یاری کردند؛ مؤمنان حقیقی فقط آنانند، برای آن‌ها آمرزش و رزق نیکو و فراوانی است.(انفال، 74)", 9, 335);
