INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("نسبت به عقاید دیگران احترام‏ می‌گذارید؟", "پیامبر به آنچه از پروردگارش به او نازل شده، ایمان آورده و مؤمنان همگی به خدا و فرشتگان و کتاب‌ها و پیامبرانش، ایمان آورده‌اند [و بر اساس ایمان استوارشان گفتند:] ما میان هیچ یک از پیامبران او فرق نمی‌گذاریم و گفتند: شنیدیم و اطاعت کردیم؛ پروردگارا، آمرزشت را می‌خواهیم و بازگشت به سوی توست.(بقرع، 285)", 9, 336);
