INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("با توجه به این که می‌توانید از تجارب استفاده کنید، چقدر شکست یا تلخ‌کامی تکراری در زندگی دارید؟", "امام صادق فرمودند: مؤمن، یاورى خوب و کم خرج است؛ با تدبیر خوب معیشت خود را تنظیم مى‏کند؛ دو بار، از یک سوراخ گزیده نمى‏شود.", 9, 337);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چه اوقاتی در زندگی انصاف را رعایت می‌کنید؟", "پیامبر خدا فرمودند: سه چیز از ایمان است انفاق کردن در تنگ‏دستى، سلام کردن به عالم، و انصاف داشتن.", 9, 338);
