INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("رضایت و خشنودی، شما را به گناه و باطل نمی‌کشاند؟", "امام باقر فرمودند: همانا مؤمن کسى است که چون راضى و خرسند باشد، رضایتش او را به گناه و باطلى وارد نکند و چون ناراضى شود از سخن حق خارجش نکند و کسى است که چون قدرت یابد قدرتش او را به آنچه حق ندارد نکشاند.", 9, 342);
