INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر ایمانتان بر اساس یقین است؟", "امیرمومنان فرمودند:... بدانید که ایمان بر چهار پایه بنا شده است؛ یقین و صبر و عدل و جهاد. یقین بر چهار شعبه است؛ شوق و ترس و زهد و انتظار. هر کس مشتاق بهشت باشد شهوات را از یاد مى‏برد و هر کس از آتش بترسد از محرّمات پرهیز مى‏کند و هر کس نسبت به دنیا زهد و بى‏اعتنایى کند مصیبت‌ها بر او آسان مى‏شود و هر کس منتظر مرگ باشد در خیرات مى‏شتابد.", 9, 344);
