INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر به مسلمانان و مستضعفان سایر کشورها علاقه دارید؟", "بگو: «ای اهل کتاب! بیایید به سوی سخنی که میان ما و شما یکسان است؛ که جز خداوند یگانه را نپرستیم و چیزی را همتای او قرار ندهیم؛ و بعضی از ما، بعضی دیگر را -غیر از خدای یگانه- به خدایی نپذیرد.» هرگاه (از این دعوت،) سرباز زنند، بگویید: «گواه باشید که ما مسلمانیم!»(آل عمران ، 64)", 26, 351);
