INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مراجعین فرد مدعی بعد از مراجعه چه احساسی دارند؟", "عبد العزيز بن مسلم گويد: در زمان علىّ بن موسى الرّضا عليهما السّلام در مرو بوديم، از آغاز ورود روز جمعه در مسجد جامع آن شهر گرد آمده بوديم و مردم درباره امامت و اختلافاتِ زياد مردم در آن مورد، بحث و گفتگو مى‏كردند، من نزد آقا و سرورم حضرت رضا- عليه السّلام- رفتم و گفتگوهاى‏ مردم را به عرض ايشان رساندم، حضرت تبسّمى كرده فرمودند:...امام همچون آب گوارا بر تشنگان است، راهنماى هدايت و منجى از هلاكت است. امام همچون آتشى است در بلندى‏هاى بيابان¬ها، كسى كه از سرما به آن آتش پناه برد، او را گرم مى‏كند، و در مهلكه‏ها راهنمائى مى‏كند... .", 27, 395);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("نظر سایر ائمه نسبت به برتری او چیست؟", "به ابى جعفر (امام باقر) عليه السّلام عرض كردم: هنگامى كه امام قائم از اهل بيت از دنيا برود آنكه پس از او مى‏آيد به چه چيز شناخته مى‏شود؟ فرمود: ... و اين كه آل محمّد به برترى او اقرار داشته باشند... .", 27, 396);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مورد نظر برای برپایی نماز در جامعه چه اقدامی انجام می دهد؟", "ابو خليفه گفت: من و ابو عبيده حذاء خدمت حضرت باقر عليه السّلام رفتيم. ... عرض كردم: ما چگونه ميتوانيم امام را بشناسيم فرمود:  كسانى كه اگر به آنها در زمين قدرت دهيم نماز بپا ميدارند ... .", 27, 397);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی نسبت به پرداخت زکات چگونه است؟", "ابو خليفه گفت: من و ابو عبيده حذاء خدمت حضرت باقر عليه السّلام رفتيم. ...عرض كردم: ما چگونه ميتوانيم امام را بشناسيم فرمود: از اين آيه ... و زكات ميدهند ... .", 27, 398);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی نسبت به دیگران در مورد نیکی های شناخته شده چه رفتاری دارد؟", "ابو خليفه گفت: من و ابو عبيده حذاء خدمت حضرت باقر عليه السّلام رفتيم. ... عرض كردم: ما چگونه ميتوانيم امام را بشناسيم فرمود: از اين آيه...و امر بمعروف ... .", 27, 399);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مورد نظر نسبت به انتشار محرمات شناخته شده در جامعه چه می کند؟", "ابو خليفه گفت: من و ابو عبيده حذاء خدمت حضرت باقر عليه السّلام رفتيم. ... عرض كردم: ما چگونه ميتوانيم امام را بشناسيم فرمود: از اين آيه... و نهى از منكر مينمايند» هر گاه يكى از ما خانواده را با اين مشخصات ديدى او امام تو است از او پیروی کن.", 27, 400);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("رفتار فرد مورد نظر با دیگران بر چه اساسی است؟", "فضل بن سكن از حضرت صادق عليه السّلام نقل كرد كه امير المؤمنين عليه السّلام فرمود: ... و امام را به ... و عدل ... بشناسيد.", 27, 401);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مورد نظر شما در انجام اعمال نیک چگونه رفتار می کند؟", "فضل بن سكن از حضرت صادق عليه السّلام نقل كرد كه امير المؤمنين عليه السّلام فرمود: ...و امام را به ... و احسان بشناسيد.", 27, 402);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("سابقه خانواده مدعی چگونه است؟", "پیامبر صلی الله علیه و آله پیش از بعثت به بیست ویژگی از ویژگی‌های پیامبران صلی الله علیه و آله موصوف بود که اگر کسی یکی از آن‌ها را داشت همان یکی برای دلالت بر ارجمندی اش کافی بود، چه رسد به کسی که هر بیست ویژگی درونش جمع شده باشد... ریشه¬دار ... .", 27, 403);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("اجداد فرد مورد نظر از چه نژادی هستند؟", "امام رضا ع از پیامبر گرامی اسلام نقل می¬کند: امامان همگی از قریش هستند", 27, 404);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("سیادت فرد مدعی در بین مردم چگونه است؟", "امام رضا ع از پیامبر گرامی اسلام نقل می¬کند: امامان همگی از قریش هستند", 27, 405);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("اجداد فرد مورد نظر به امام حسین می رسد؟", "حسن بن جهم گفت: روزى به مجلس مأمون رفتم حضرت رضا عليه السّلام نيز حضور داشت. دانشمندان و عقيده‏شناسان از فرقه‏هاى مختلف اجتماع كرده بودند، يكى از دانشمندان از حضرت رضا عليه السّلام پرسيد آقا بفرمائيد مدعى امامت بايد داراى چه شرايطى باشد. در جواب فرمود:... و پيشوايان از فرزندان حضرت حسين عليه السّلام تا روز قيامت است... .", 27, 406);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی از قبیله معروف عربی است؟", "ضرار از هشام بن حكم پرسيد: چه نشانه ای براى امام بعد از پيامبر هست. هشام پاسخ داد: هشت دليل وجود دارد كه چهار دليل آن در شخصيت خانوادگى اوست و چهار دليل در شخص اوست اما آن چهار دليل كه در نژاد اوست 1- داراى قبيله‏اى معروف است... .", 27, 407);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی دارای خویشاوندی معروفی است؟", "ضرار از هشام بن حكم پرسيد: چه نشانه ای براى امام بعد از پيامبر هست. هشام پاسخ داد: هشت دليل وجود دارد كه چهار دليل آن در شخصيت خانوادگى اوست و چهار دليل در شخص اوست اما آن چهار دليل كه در نژاد اوست...3- شهرت نسبى... .", 27, 408);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("خانواده مدعی مشهور و جافتاده و وزین است؟", "ضرار از هشام بن حكم پرسيد: چه نشانه ای براى امام بعد از پيامبر هست. هشام پاسخ داد: هشت دليل وجود دارد كه چهار دليل آن در شخصيت خانوادگى اوست و چهار دليل در شخص اوست اما آن چهار دليل كه در نژاد اوست... 4- شهرت خانوادگى ... .", 27, 409);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مورد نظر شما پسر بزرگ یا پسر بعدی با فضیلت امام قبل است؟", "بزنطى گفت: از حضرت رضا عليه السّلام پرسيدند چگونه ميتوان امام را پس از فوت امام قبل شناخت.فرمود: امام داراى علامتهائى است: بزرگترين فرزند پدر خويش است... .", 27, 410);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("ادعای امامت فرد مورد نظر در چه زمانی نسبت به خروج یمانی است؟", "مَيْمُونٍ بَانِ از امام صادق عليه السّلام روايت كند كه فرمود: پيش از قيام‏ قائم عليه السّلام پنج نشانه ظاهر شود: يمانى ... .", 27, 411);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("ادعای امامت مدعی در چه زمانی نسبت به خروج سفیانی است؟", "مَيْمُونٍ بَانِ از امام صادق عليه السّلام روايت كند كه فرمود: پيش از قيام‏ قائم عليه السّلام پنج نشانه ظاهر شود: ... و سفيانى ... .", 27, 412);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("ادعای امامت فرد مدعی در چه زمانی نسبت به خروج خراسانی است؟", "ابو بصير از امام صادق عليه السّلام روايت كرده گويد: «به آن حضرت عرض كردم:فدايت گردم خروج قائم عليه السّلام چه زمانى خواهد بود؟ فرمود: ... و خروج خراسانى... .", 27, 413);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("ادعای امامت فرد مورد نظر در چه زمانی نسبت به ندای آسمانی است؟", "مَيْمُونٍ بَانِ از امام صادق عليه السّلام روايت كند كه فرمود: پيش از قيام‏ قائم عليه السّلام پنج نشانه ظاهر شود: ... و منادى آسمانى ... .", 27, 414);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("ادعای امامت مدعی در چه زمانی نسبت به فرو رفتن زمین در بیداء است؟", "مَيْمُونٍ بَانِ از امام صادق عليه السّلام روايت كند كه فرمود: پيش از قيام‏ قائم عليه السّلام پنج نشانه ظاهر شود: ...و فرو رفتن زمين در بيداء ... .", 27, 415);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("ادعای امامت فرد مدعی در چه زمانی نسبت به کشتن نفس زکیّه ادعای است؟", "مَيْمُونٍ بَانِ از امام صادق عليه السّلام روايت كند كه فرمود: پيش از قيام‏ قائم عليه السّلام پنج نشانه ظاهر شود: ...و كشتن نفس زكيّه.", 27, 416);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("بعد از ادعای امامت فرد مورد نظر حضرت عیسی کجاینند؟", "امام رضا ع فرمود: هرگاه مهدی از اولاد من خروج کند عیسی بن مریم علیهما السلام  نازل می‌شود و پشت سر او نماز می‌خواند", 27, 417);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مورد نظر می تواند مرده های را زنده کند؟", "ثمالى از حضرت زين العابدين عليه السّلام نقل كرد كه گفتم ... ائمه عليهم السّلام مرده زنده ميكنند ... .", 27, 418);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی می تواند بیمار پیسی را شفا دهد؟", "ثمالى از حضرت زين العابدين عليه السّلام نقل كرد كه گفتم ... كور و پيس را شفا ميدهند ... .", 27, 420);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی می تواند بیماری کور مادرزاد را شفا دهد؟", "ثمالى از حضرت زين العابدين عليه السّلام نقل كرد كه گفتم ...  كور و پيس را شفا ميدهند ... .", 27, 421);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مورد نظر می تواند فردی که همه پیری او را دیده اند جوان کند؟", "حبابه والبيه (نام زنى است از والبه يمن) گويد...  من از دريافت نشانه امامت مأيوس شدم، حضرت با انگشت سبابه بمن اشاره كرد، جوانی بمن برگشت، ...  .", 27, 422);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مورد نظر می تواند موی سفید افراد پیر را مانند جوانان سیاه برویاند؟", "حبابه والبيه (نام زنى است از والبه يمن) گويد...  من از دريافت نشانه امامت مأيوس شدم، حضرت با انگشت سبابه بمن اشاره كرد، جوانی بمن برگشت، ...  .", 27, 422);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی امامت بر همه زبان ها مسلط است؟", "ابو الجارود گفت: از حضرت باقر عليه السّلام پرسيدم به چه دليل مى‏توان امام را شناخت؟ فرمود: با چند امتياز ... و با هر زبان و لغتى با مردم سخن ميگويد.", 27, 423);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی بدون استفاده از مترجم همه زبان ها را به درستی درک می کند؟", "ابو الجارود گفت: از حضرت باقر عليه السّلام پرسيدم به چه دليل مى‏توان امام را شناخت؟ فرمود: با چند امتياز ... و با هر زبان و لغتى با مردم سخن ميگويد.", 27, 423);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی شما همه لهجه ها را مسلط است؟", "ابو الجارود گفت: از حضرت باقر عليه السّلام پرسيدم به چه دليل مى‏توان امام را شناخت؟ فرمود: با چند امتياز ... و با هر زبان و لغتى با مردم سخن ميگويد.", 27, 424);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی بدون لهجه به همه زبان ها حرف می زند؟", "ابو الجارود گفت: از حضرت باقر عليه السّلام پرسيدم به چه دليل مى‏توان امام را شناخت؟ فرمود: با چند امتياز ... و با هر زبان و لغتى با مردم سخن ميگويد.", 27, 424);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مورد نظر هنگام تولد شهادتین را بلند گفته است؟", "زراره از حضرت باقر نقل كرد كه فرمود: امام داراى ده علامت است: ... وقتى روى زمين قرار گرفت دو دست بر زمين ميگذارد و صدا بشهادتين بلند ميكند. ... .", 27, 425);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("همیشه بر مدعی وحی نازل می شود؟", "حسن بن علىّ بن فضّال از پدرش از امام رضا عليه السّلام چنين نقل كرده است: امام نشانه¬هایی دارد: ... محدّث است (يعنى صداى فرشته وحى را مى‏شنود ولى آن را نمى‏بيند)، ... .", 27, 426);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("هنگامی که فرد مدعی دعا می کند مستجاب می شود؟", "حسن بن علىّ بن فضّال از پدرش از امام رضا عليه السّلام چنين نقل كرده است: امام نشانه¬هایی دارد: ... دعايش مستجاب است، به گونه‏اى كه اگر دعا كند كه صخره‏اى دو نيم شود، دو نيم خواهد شد، ... .", 27, 427);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("تسلط فرد مورد نظر شما به محتوای کتب آسمانی سابق چگونه است؟", "حسين بن علوان از حضرت صادق عليه السّلام نقل كرد كه فرمود:خداوند پيامبران اولو العزم را بوسيله علم بر انبياء مزيت بخشيده و علم آنها را بارث بما داده و ما را بر آنها در فضلشان برترى بخشيده. پيامبر اكرم چيزهائى ميدانست كه آنها نميدانستند و ما داراى علم پيامبريم و براى شيعيان خود روايت مى‏كنيم پس هر كس از آنها بپذيرد بهترين ايشان است و هر كجا باشيم پس شيعيان ما با ما هستند.", 27, 428);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی نسبت به بر طرف کردن نیازهای مردم چگونه است؟", "هشام بن حكم گفت: از حضرت صادق عليه السّلام در سرزمین منى از پانصد حرف از علم كلام سؤال كردم. من ميگفتم: چنين و چنين ميگويند او مي فرمود: بگو چنين و چنان، گفتم: فدايت شوم از حلال و حرام و قرآن ميدانم شما آگاه هستيد و از همه مردم وارد تريد اما اين  علم كلام است. فرمود: هشام مگر شك دارى، كسى كه شك كند كه خداوند حجتى بر مردم قرار دهد كه در نزد او نيازهاى مردم نباشد بر خدا بهتان زده.", 27, 429);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("تسلط مدعی بر علوم مختلف دین چگونه است؟", "هشام بن حكم گفت: از حضرت صادق عليه السّلام در سرزمین منى از پانصد حرف از علم كلام سؤال كردم. من ميگفتم: چنين و چنين ميگويند او مي فرمود: بگو چنين و چنان، گفتم: فدايت شوم از حلال و حرام و قرآن ميدانم شما آگاه هستيد و از همه مردم وارد تريد اما اين  علم كلام است. فرمود: هشام مگر شك دارى، كسى كه شك كند كه خداوند حجتى بر مردم قرار دهد كه در نزد او نيازهاى مردم نباشد بر خدا بهتان زده.", 27, 429);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی در جواب پرسش هایی که از او می شود چگونه پاسخ می دهد؟", "امام کاظم ع فرمود: ... خداوند در روى زمين حجتى قرار نمى‏دهد كه از او چيزى بپرسند و بگويد نمى‏دانم.", 27, 430);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی سوالات قرآنی را پاسخ می دهد؟", "سليمان بن مهران از حضرت صادق عليه السّلام نقل كرد: كه ده خصلت از صفات امام است: 1- عصمت. 2- نص و تصريح. 3- اعلم مردم باشد. 4- پرهيزگارترين آنها. 5- داناترين ايشان به كتاب خدا. 6- به او آشكارا وصيت كرده باشند. 7- داراى اعجاز و دليل باشد. 8- چشمش ميخوابد ولى دلش بيدار است. 9- سايه ندارد. 10- از پشت سر چنان مى‏بيند كه از روبرو مشاهده‏ كند.", 27, 431);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی در جواب پرسش هایی که از او می شود چگونه پاسخ می دهد؟", "به ابى جعفر (امام باقر) عليه السّلام عرض كردم هنگامى كه امام قائم از اهل بيت از دنيا برود آنكه پس از او مى‏آيد به چه چيز شناخته مى‏شود؟ فرمود: ...و از چيزى كه ميان‏ دو طرف زمين (پشت و روى آن) هست از او پرسیده نشود مگر آنكه پاسخ بدهد.", 27, 432);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("وقتی نزد مدعی می روید نظر او در مورد چیزی را که فراموش کردید چیست؟", "اسحاق بن يعقوب گويد: از شيخ عمرى- رضى اللَّه عنه- شنيدم كه مى‏گفت: با مردى شهرى مصاحبت داشتم و به همراه او مالى براى قائم عليه السّلام بود و آن را براى او فرستاد و آن مال را به او برگردانيد و به او گفتند: حق عموزادگانت را كه بالغ بر چهار صد درهم است از آن خارج كن، آن مرد متحيّر و مبهوت و متعجّب گرديد و حسابرسى كرد و در دستش مزرعه‏اى بود كه متعلّق به عموزادگانش بود كه مقدارى از آن را به آنها تسليم كرده بود ولى بقيه آن را به آنها واگذارنكرده بود و سهم آنان از آن مال چنان كه فرموده بود چهار صد درهم گرديد، پس آن را از مال خود خارج ساخت و باقى را فرستاد و او آن را پذيرفت.", 27, 433);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی سوالات ذهنی را می تواند تشخیص دهد؟", "اسحاق بن يعقوب گويد: از شيخ عمرى- رضى اللَّه عنه- شنيدم كه مى‏گفت: با مردى شهرى مصاحبت داشتم و به همراه او مالى براى قائم عليه السّلام بود و آن را براى او فرستاد و آن مال را به او برگردانيد و به او گفتند: حق عموزادگانت را كه بالغ بر چهار صد درهم است از آن خارج كن، آن مرد متحيّر و مبهوت و متعجّب گرديد و حسابرسى كرد و در دستش مزرعه‏اى بود كه متعلّق به عموزادگانش بود كه مقدارى از آن را به آنها تسليم كرده بود ولى بقيه آن را به آنها واگذارنكرده بود و سهم آنان از آن مال چنان كه فرموده بود چهار صد درهم گرديد، پس آن را از مال خود خارج ساخت و باقى را فرستاد و او آن را پذيرفت.", 27, 433);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی زبان حیوانات را می فهمد؟", "ابو بصير از موسى بن جعفر عليه السّلام نقل كرد: كه من خدمت ايشان رسيدم گفتم: فدايت شوم امام را چگونه مى‏توان شناخت، فرمود: با چند امتياز: اول تصريح پدرش به امامت او و معرفى به مردم كه امام است تا حجت بر آنها باشد زيرا پيامبر اكرم علي عليه السّلام را به منصب امامت تعيين كرد و به مردم معرفى نمود، همين طور ائمه عليهم السّلام نيز جانشين خود را تعيين ميكنند و به مردم معرفى مينمايند. از او سؤال كنند جواب ميدهد اگر ساكت باشند او خود ايشان را خبر ميدهد و از آنچه فردا اتفاق خواهد افتاد اطلاع ميدهد و با تمام زبانها با مردم سخن ميگويد. آنگاه رو به من نموده فرمود: هم اكنون بتو علامتى نشان ميدهم كه اطمينان حاصل كنى. بخدا قسم چيزى نگذشت كه مردى از اهالى خراسان وارد شد او بزبان عربى صحبت كرد امام عليه السّلام بفارسى جوابش را داد. خراسانى گفت: آقا خدا نگهدار شما باشد چيزى مانع صحبت كردن من به فارسى نشد جز اينكه گمان ميكردم شما فارسى خوب نميدانيد. فرمود: سبحان اللَّه اگر من نتوانم جواب ترا بدهم پس چه فضيلتى بر تو دارم. سپس فرمود: ابا محمّد براى امام زبان هيچ يك از مردم و پرنده‏ها و چهارپايان و نه هر صاحب روحى مخفى نيست. با چنين دليلى مى‏توان امام را شناخت، اگر در او چنين امتيازهايى نباشد امام نيست.", 27, 434);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی امامت می تواند از امور آینده خبر بدهد؟", "ابو بصير از موسى بن جعفر عليه السّلام نقل كرد: كه من خدمت ايشان رسيدم گفتم: فدايت شوم امام را چگونه مى‏توان شناخت، فرمود: با چند امتياز: اول تصريح پدرش به امامت او و معرفى به مردم كه امام است تا حجت بر آنها باشد زيرا پيامبر اكرم علي عليه السّلام را به منصب امامت تعيين كرد و به مردم معرفى نمود، همين طور ائمه عليهم السّلام نيز جانشين خود را تعيين ميكنند و به مردم معرفى مينمايند. از او سؤال كنند جواب ميدهد اگر ساكت باشند او خود ايشان را خبر ميدهد و از آنچه فردا اتفاق خواهد افتاد اطلاع ميدهد و با تمام زبانها با مردم سخن ميگويد. آنگاه رو به من نموده فرمود: هم اكنون بتو علامتى نشان ميدهم كه اطمينان حاصل كنى. بخدا قسم چيزى نگذشت كه مردى از اهالى خراسان وارد شد او بزبان عربى صحبت كرد امام عليه السّلام بفارسى جوابش را داد. خراسانى گفت: آقا خدا نگهدار شما باشد چيزى مانع صحبت كردن من به فارسى نشد جز اينكه گمان ميكردم شما فارسى خوب نميدانيد. فرمود: سبحان اللَّه اگر من نتوانم جواب ترا بدهم پس چه فضيلتى بر تو دارم. سپس فرمود: ابا محمّد براى امام زبان هيچ يك از مردم و پرنده‏ها و چهارپايان و نه هر صاحب روحى مخفى نيست. با چنين دليلى مى‏توان امام را شناخت، اگر در او چنين امتيازهايى نباشد امام نيست.", 27, 435);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مورد نظر شما از آنچه در دل مردم می گذرد آگاهی دارد؟", "نقل شده است كه محمّد بن ابراهيم بن مهزيار در حال شكّ و ترديد وارد عراق شد و اين توقيع براى وى صادر گرديد: به مهزيارى بگو آنچه را از دوستان آن سامان حكايت كردى فهميديم، به آنها بگو آيا قول خداى تعالى را نشنيديد كه مى‏فرمايد: يا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَ أَطِيعُوا الرَّسُولَ وَ أُولِي الْأَمْرِ مِنْكُمْ‏  آيا اين دستور تا روز قيامت نيست؟ آيا خداى تعالى پناهگاههايى براى شما قرار نداده است كه بدان پناهنده شويد؟ آيا از زمان آدم عليه السّلام تا زمان‏امام گذشته ابو محمّد صلوات اللَّه عليه اَعلام هدايت براى شما قرار نداده است؟ و اگر علَمى نهان شد علَمى آشكار نگرديد و اگر ستاره‏اى افول كرد ستاره‏اى ندرخشيد؟ و چون خداى تعالى ابو محمّد را قبض روح كرد پنداشتيد كه او رابطه بين خود و خلقش را قطع كرده است؟ هرگز چنين نبوده و تا روز قيامت چنين نخواهد بود در آن روز امر خداى تعالى ظاهر شود و آنان ناخشنود باشند. اى محمّد بن ابراهيم! براى چيزى كه به خاطر آن آمدى شكّ به خود راه مده كه خداى تعالى زمين را از حجّت خالى نگذارد، آيا پدرت پيش از وفاتش به تو نگفت: هم اكنون بايد كسى را حاضر كنى كه اين دينارهايى را كه نزد من است وزن كند و چون دير شد و شيخ بر جان خود ترسيد كه به زودى بميرد به تو گفت:آنها را تو خود وزن كن و كيسه بزرگى به تو داد و تو سه كيسه داشتى و يك كيسه كه دينارهاى گوناگون در آن بود، آنها را وزن كردى و شيخ با خاتم خود آنها را مهر كرد و گفت تو هم آنها را مهر كن، اگر زنده ماندم كه خود مى‏دانم چه كنم و اگر مردم، تو اوّلا در باره خود و ثانيا در باره من از خدا بپرهيز و مرا خلاص كن و چنان باش كه به تو گمان دارم، خدا تو را رحمت كند آن دينارهايى را كه از ما بين‏نقدين از حساب ما جدا كردى و ده و اندى دينار است بيرون كن و از جانب خود آنها را مسترد كن كه زمانه بسيار سخت است و حَسْبُنَا اللَّهُ وَ نِعْمَ الْوَكِيلُ‏. محمّد بن ابراهيم گويد: براى ديدار به عسكر رفتم و قصد ناحيه مقدّسه را داشتم، زنى مرا ديد و گفت: آيا تو محمّد بن ابراهيمى؟ گفتم: آرى، گفت: بازگرد كه در اين هنگام به مقصود نمى‏رسى و شب هنگام مراجعت كن كه در به رويت باز است داخل در سرا شو و قصد آن اتاقى را كن كه چراغش روشن است و من هم چنان كردم و قصد آن در را كردم و به ناگاه ديدم كه باز است داخل در سرا شدم و قصد همان اتاقى را كردم كه توصيف كرده بود و در اين بين كه خود را ميان دو قبر ديدم و گريه و ناله مى‏كردم ناگهان صدايى را شنيدم كه مى‏گفت: اى محمّد! تقواى الهى پيشه ساز و از گذشته توبه كن كه كار بزرگى را عهده‏دار شدى.", 27, 436);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی می داند که در زندگی افراد چه می گذرد؟", "محمد بن على بن ابراهيم بن موسى بن جعفر عليهما السلام گويد: كار بر ما سخت و دشوار گرديد، پدرم به من گفت: با من بيا نزد اين مرد يعنى ابو محمد (امام عسكرى عليه السلام) برويم، زيرا از او جوانمردى شنيده مى‏شود، گفتم: او را ميشناسى؟ گفت: نميشناسم و هرگز او را نديده‏ام، سپس آهنگ او كرديم، پدرم در بين راه به من ميگفت: احتياج به 500 درهم داريم. اگر به ما بدهد 200 درهمش را براى پوشاك و 200 درهمش را براى بدهى و 100 درهمش را براى مخارج صرف ميكنيم، من با خود گفتم: كاش به من هم 300 درهم بدهد كه با 100 درهمش الاغى بخرم و 100 درهمش براى خرجى و 100درهم ديگرش براى پوشاك باشد تا بكوهستان (همدان و اطرافش) بروم.چون به در خانه رسيديم، غلامش آمد و گفت: على بن ابراهيم با پسرش محمد در آيند، چون وارد شديم و سلام كرديم، به پدرم فرمود: اى على! چرا تا كنون نزد ما نيامدى؟ پدرم گفت: آقاى من! خجالت ميكشيدم با اين وضع به ملاقات شما آيم، چون از نزدش بيرون رفتيم، غلامش آمد و به پدرم كيسه پولى داد و گفت: اين 500 درهم است كه 200 آن براى پوشاك و 200 آن براى بدهى و 100 آن براى خرجيت باشد. و كيسه‏ئى به من داد و گفت: اين 300 درهم است، 100 درهمش براى خريد الاغ و 100 درهمش براى پوشاك و 100 درهمش براى مخارجت باشد. و بكوهستان نرو، بلكه به سوراء برو.او به سوراء رفت و با زنى ازدواج كرد و اكنون هزار دينار عايدى املاك دارد، با وجود اين واقفى مذهب است (يعنى هفت امامى است و عقيده دارد موسى بن جعفر عليه السلام نمرده و او امام قائم است) محمد بن ابراهيم گويد: به او گفتم: واى بر تو! مگر دليلى روشن‏تر از اين ميخواهى؟!! (كه امام يازدهم از دل تو آگاه باشد و بمقدار احتياجت به تو كمك كند) او گفت: اين امريست كه بدان عادت كرده‏ايم (يعنى كيش و مذهب خانوادگى ماست)..", 27, 437);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی امامت می تواند با انگشتر روی یک سنگ سخت نشان امامت خود را بدون فشار حک کند؟", "حبابه والبيه (نام زنى است از والبه يمن) گويد: امير المؤمنين عليه السلام را در محل پيش قراولان لشكر ديدم كه با تازيانه دوسرى كه همراه داشت فروشندگان ماهى جرى (بى‏فلس) و مار ماهى و ماهى زمار را (كه فروش آنها حرامست) ميزد و ميفرمود: اى فروشندگان مسخ‏شده‏هاى بنى اسرائيل و لشكر بنى مروان! فرات بن احنف نزد حضرت ايستاد و گفت: يا امير المؤمنين لشكر بنى مروان كيانند فرمود: مردمى كه ريشها را ميتراشيدند و سبيلها را تاب ميدادند سپس مسخ شدند. (فرات گويد) من گوينده‏اى را خوش بيان‏تر از او نديده بودم. از دنبالش ميرفتم تا در جلو خان مسجد نشست، باو عرض¬كردم: دليل بر امامت چيست خدايت رحمت كند؟ فرمود: آن سنگريزه را بياور- و با دست اشاره بسنگريزه‏ئى كرد- آن را نزدش آوردم، پس با خاتمش آن را مهر كرد و سپس به من فرمود: اى حبابه: هر گاه كسى ادعاى امامت كرد و توانست چنان كه ديدى مهر كند، بدان كه او امامي است كه اطاعتش واجب است و نيز امام هر چه را بخواهد، از او پنهان نگردد. حبابه گويد: من رفتم تا زمانى كه امير المؤمنين عليه السلام وفات كرد، نزد امام حسن عليه السلام آمدم، زمانى كه آن حضرت در مسند امير المؤمنين عليه السلام نشسته و مردم از او سؤال ميكردند. فرمود: اى حبابه والبيه عرضكردم: آرى، مولاى من؛ فرمود: آنچه همراه دارى بياور، من آن سنگريزه را به او دادم، حضرت براى من بر آن مهر نهاد چنان كه امير المؤمنين عليه السلام مهر نهاد. سپس نزد حسين عليه السلام آمدم، زمانى كه در مسجد پيغمبر صلّى اللَّه عليه و آله بود، مرا پيش خواند و خوش آمد گفت، سپس فرمود: در ميان نشانه امامت آنچه را هم تو ميخواهى هست، دليل امامت را ميخواهى؟ گفتم: آرى، آقاى من! فرمود: آنچه همراه دارى بياور، سنگريزه را به آن حضرت دادم، او هم براى من بر آن مهر نهاد. سپس نزد على بن الحسين عليه السلام آمدم و از پيرى به آنجا رسيده بودم كه مرا رعشه گرفته بود و من آن زمان 113 سال براى خود مي¬شمردم. آن حضرت را ديدم ركوع و سجود ميكند و مشغول عبادت است. من از دريافت نشانه امامت مأيوس شدم، حضرت با انگشت سبابه بمن اشاره كرد، جوانیه من برگشت، گفتم: آقاى من از دنيا چقدر گذشته و چقدر باقى مانده؟ فرمود: اما نسبت به گذشته آرى و اما نسبت به باقيمانده، نه (گذشته را ميتوان معلوم كرد ولى باقيمانده را كسى را نميداند) سپس فرمود: آنچه همراه دارى بياور. من سنگريزه را به او دادم، حضرت بر آن مهر نهاد. سپس آن را به امام باقر عليه السلام دادم او هم برايم مهر كرد سپس نزد امام صادق عليه السلام آمدم او هم برايم مهر كرد: سپس خدمت ابو الحسن موسى بن جعفر عليه السلام آمدم، او هم برايم مهر كرد، سپس خدمت حضرت رضا عليه السلام آمدم، او هم برايم مهر كرد و چنانچه محمد بن هشام نقل كرده، حبابه بعد از آن، 9 ماه ديگر هم زنده بود.", 27, 438);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی در زندگی شخصی به کسی ظلم کرده است؟", "عبد الحميد بن نصر گفت: حضرت صادق عليه السّلام فرمود: منكر امام مفترض الطاعه مي¬شوند و از او كناره مي¬گيرند، به خدا سوگند در روى زمين مقامى بالاتر از واجب الاطاعه نيست؛ ابراهيم مدتها بود كه از طرف خدا به او وحى مي¬شد ولى واجب الاطاعه (اطاعت او واجب باشد) نبود تا بالاخره خداوند خواست او را گرامى به دارد و عظمت بخشد، فرمود إِنِّي جاعِلُكَ لِلنَّاسِ إِماماً(من تو را برای مردم امام قرار دادم) ابراهيم فهميد چه مقامى به او عنايت شده گفت‏ وَ مِنْ ذُرِّيَّتِي‏ (آيا در فرزندان من نيز هست) فرمود لا يَنالُ عَهْدِي الظَّالِمِينَ‏ عهد من به ستمگران نمی¬رسد، حضرت صادق فرمود: خداوند تصريح كرده كه اين مقام شايسته ذريه تو است نه ديگران كه ستمگر می¬باشند.", 27, 439);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی در زندگی گذشته آلوده به حرام شده است؟", "صلاحيت امامت را ندارد مگر شخصى كه در او سه امتياز باشد: پرهيزكاری که او را از ارتکاب حرام باز دارد، و بردباری که به سبب او مالک خشم خود شود، و آنچنان حسن رفتار و رهبرى بر مردم را داشته باشد كه براى آنها همچون پدرى مهربان باشد.", 27, 440);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی با منجمان ارتباط دارد؟", "پیامبر صلی الله علیه و آله پیش از بعثت به بیست ویژگی از ویژگی‌های پیامبران صلی الله علیه و آله موصوف بود که اگر کسی یکی از آن‌ها را داشت همان یکی برای دلالت بر ارجمندی اش کافی بود، چه رسد به کسی که هر بیست ویژگی درونش جمع شده باشد: ایشان پیامبری ... با هیچ منجم نیامیخته بود ... .", 27, 441);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی با پیشگویان ارتباط دارد؟", "پیامبر صلی الله علیه و آله پیش از بعثت به بیست ویژگی از ویژگی‌های پیامبران صلی الله علیه و آله موصوف بود که اگر کسی یکی از آن‌ها را داشت همان یکی برای دلالت بر ارجمندی اش کافی بود، چه رسد به کسی که هر بیست ویژگی درونش جمع شده باشد: ایشان پیامبری ... با هیچ کاهنی نیامیخته بود... .", 27, 443);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی با فالگیران ارتباط دارد؟", "پیامبر صلی الله علیه و آله پیش از بعثت به بیست ویژگی از ویژگی‌های پیامبران صلی الله علیه و آله موصوف بود که اگر کسی یکی از آن‌ها را داشت همان یکی برای دلالت بر ارجمندی اش کافی بود، چه رسد به کسی که هر بیست ویژگی درونش جمع شده باشد: ایشان پیامبری ... که با هیچ فالگیری نیامیخته بود.... .", 27, 444);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی همه دستورات خداوند را امتثال کرده است؟", "وَ در اجراى احكام خدا او را فتورى دست نداده و نه جاهليت را درك كرده باشد و بايد در هر زمانى با اين امتيازها امامى تا روز قيامت قيام به امر نمايد.", 27, 445);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی در مورد محرمات زبانی چگونه عمل می کند؟", "حفص بن بخترى از حضرت صادق عليه السّلام نقل كرد كه به آن جناب گفتند: به چه دليل ميتوان امام را شناخت فرمود: به وصيت آشكار و به فضل، همانا احدى نمي¬تواند بر امام در مورد زبان و شكم و شهوت خورده بگيرد مثلا بگويد: دروغگو است و اموال مردم را مى‏خورد و از اين قبيل.", 27, 446);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی در مورد محرمات خوراکی و آشامیدنی چگونه عمل می کند؟", "حفص بن بخترى از حضرت صادق عليه السّلام نقل كرد كه به آن جناب گفتند: به چه دليل ميتوان امام را شناخت فرمود: به وصيت آشكار و به فضل، همانا احدى نمي¬تواند بر امام در مورد زبان و شكم و شهوت خورده بگيرد مثلا بگويد: دروغگو است و اموال مردم را مى‏خورد و از اين قبيل.", 27, 447);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی در مورد محرمات جنسی چگونه عمل می کند؟", "حفص بن بخترى از حضرت صادق عليه السّلام نقل كرد كه به آن جناب گفتند: به چه دليل ميتوان امام را شناخت فرمود: به وصيت آشكار و به فضل، همانا احدى نمي¬تواند بر امام در مورد زبان و شكم و شهوت خورده بگيرد مثلا بگويد: دروغگو است و اموال مردم را مى‏خورد و از اين قبيل.", 27, 448);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی چه رفتاری نسبت به آداب و رسوم جاهلیت دارد؟", "وَ در اجراى احكام خدا او را فتورى دست نداده و نه جاهليت را درك كرده باشد و بايد در هر زمانى با اين امتيازها امامى تا روز قيامت قيام به امر نمايد.", 27, 449);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("چقدر در زندگی مدعی کاستی و بدی یا خوبی و خیر دیده اید؟", "سزاوار ترین مردم نسبت به آنها می باشد", 27, 450);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فتواهای مدعی چگونه است؟", "اسماعيل بن جابر از حضرت صادق عليه السّلام نقل می کند كه امير المؤمنين عليه السّلام فرمود: امامى كه شايسته مقام امامت است داراى علاماتى است از آن جمله اينكه از تمام گناهان كوچك و بزرگ معصوم باشد لغزشى در فتوى نداشته باشد و خطا در جواب نكند سهو و نسيان ندارد و لهو و بازى در امور دنيا ندارد. دوم: داناترين مردم است به حلال و حرام خدا و انواع احكام و امر و نهى پروردگار و تمام آنچه مردم به آن نياز دارند مردم به او محتاجند و او از آنها بى‏نياز است. سوم: بايد شجاعترين مردم باشد زيرا او فرمانرواى مؤمنين است و از او پيروى ميكنند و اگر فرار كند در جنگ به واسطه فرار او، همه فرار ميكنند. چهارم: بايد سخاوتمندترين مردم باشد گرچه تمام مردم روى زمين بخيل باشند زيرا در صورتى كه بر او بخل تسلط يابد نسبت به اموال مسلمين كه‏ در اختيار اوست بخل ميورزد. پنجم: عصمت از تمام گناهان است با همين امتياز از ساير مردم مشخص مى‏شود كه معصوم نيستند زيرا در صورتى كه معصوم نباشد ممكن است مرتكب گناهانى كه ساير مردم ميشوند بشود از قبيل گناهان موجب هلاكت و شهوترانيها وقتى چنين كارهائى را كرد بايد يكى بر او حد جارى نمايد در چنين صورتى امام، مأموم مى‏شود. جايز نيست امام داراى چنين خصوصياتى باشد. اما اينكه بايد داناترين مردم باشد براى اينست كه اگر عالم نباشد امكان دارد احكام و حدود را تغيير دهد و قضاياى مشكل او را به اختلاف اندازد به طورى كه نتواند جواب آن را بدهد و اگر جواب دهد مرتبه بعد بر خلاف آن فتوى دهد. اما اينكه بايد شجاعترين مردم باشد بهمان دليلى است كه قبلا گفتيم اگر فرار كند خود را مشمول خشم پروردگار كرده و اين براى امام صحيح نيست اما اينكه بايد سخى‏ترين مردم باشد باز به همان دليلى كه قبلا ذكر شد براى امام بخل شايسته نيست. دليل را ذكر نموده تا آنجا كه در رد اشخاصى كه قياس در دين را حلال ميشمارند فرموده است: اينكه چنين اشخاصى متوسل به قياس ميشوند به واسطه آن است كه از اقامه احكام مطابق كتاب خدا عاجزند و براى اخذ احكام نرفته‏اند به در خانه كسانى كه اطاعت آنها بر مردم واجب است و خطا و لغزش و نسيان ندارند و كتاب خدا بر آنها نازل گرديده و امت مامورند در احكام مشتبه خود به آنها مراجعه كنند. و به جای آن به واسطه عشقى كه بزر و زيور دنيا داشتند از پى رياست رفتند و راه اجداد خود را پيمودند. آنها كه مدعى مقام اولياء شدند به ناچار ادعا كردند راى و قياس واجب است.", 27, 451);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مدعی در زندگی دچار سهو و نسیان می شود؟", "اسماعيل بن جابر از حضرت صادق عليه السّلام نقل می کند كه امير المؤمنين عليه السّلام فرمود: امامى كه شايسته مقام امامت است داراى علاماتى است از آن جمله اينكه از تمام گناهان كوچك و بزرگ معصوم باشد لغزشى در فتوى نداشته باشد و خطا در جواب نكند سهو و نسيان ندارد و لهو و بازى در امور دنيا ندارد. دوم: داناترين مردم است به حلال و حرام خدا و انواع احكام و امر و نهى پروردگار و تمام آنچه مردم به آن نياز دارند مردم به او محتاجند و او از آنها بى‏نياز است. سوم: بايد شجاعترين مردم باشد زيرا او فرمانرواى مؤمنين است و از او پيروى ميكنند و اگر فرار كند در جنگ به واسطه فرار او، همه فرار ميكنند. چهارم: بايد سخاوتمندترين مردم باشد گرچه تمام مردم روى زمين بخيل باشند زيرا در صورتى كه بر او بخل تسلط يابد نسبت به اموال مسلمين كه‏ در اختيار اوست بخل ميورزد. پنجم: عصمت از تمام گناهان است با همين امتياز از ساير مردم مشخص مى‏شود كه معصوم نيستند زيرا در صورتى كه معصوم نباشد ممكن است مرتكب گناهانى كه ساير مردم ميشوند بشود از قبيل گناهان موجب هلاكت و شهوترانيها وقتى چنين كارهائى را كرد بايد يكى بر او حد جارى نمايد در چنين صورتى امام، مأموم مى‏شود. جايز نيست امام داراى چنين خصوصياتى باشد. اما اينكه بايد داناترين مردم باشد براى اينست كه اگر عالم نباشد امكان دارد احكام و حدود را تغيير دهد و قضاياى مشكل او را به اختلاف اندازد به طورى كه نتواند جواب آن را بدهد و اگر جواب دهد مرتبه بعد بر خلاف آن فتوى دهد. اما اينكه بايد شجاعترين مردم باشد بهمان دليلى است كه قبلا گفتيم اگر فرار كند خود را مشمول خشم پروردگار كرده و اين براى امام صحيح نيست اما اينكه بايد سخى‏ترين مردم باشد باز به همان دليلى كه قبلا ذكر شد براى امام بخل شايسته نيست. دليل را ذكر نموده تا آنجا كه در رد اشخاصى كه قياس در دين را حلال ميشمارند فرموده است: اينكه چنين اشخاصى متوسل به قياس ميشوند به واسطه آن است كه از اقامه احكام مطابق كتاب خدا عاجزند و براى اخذ احكام نرفته‏اند به در خانه كسانى كه اطاعت آنها بر مردم واجب است و خطا و لغزش و نسيان ندارند و كتاب خدا بر آنها نازل گرديده و امت مامورند در احكام مشتبه خود به آنها مراجعه كنند. و به جای آن به واسطه عشقى كه بزر و زيور دنيا داشتند از پى رياست رفتند و راه اجداد خود را پيمودند. آنها كه مدعى مقام اولياء شدند به ناچار ادعا كردند راى و قياس واجب است.", 27, 452);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مورد نظر در زندگی مرتکب کارهای بیهوده و بازی های می شود؟", "صفوان جمال گويد: از امام صادق عليه السلام در باره صاحب امر امامت پرسيدم، فرمود:صاحب اين امر بازى و بيهوده‏گرى نكند، آنگاه ابو الحسن موسى كه كودك بود و بزغاله‏اى مكى همراه داشت و به او مي¬گفت: « «پروردگارت را سجده كن» آمد، امام صادق عليه السلام او را در آغوش كشيد و فرمود:پدر و مادرم فداى كسى كه بازى و بيهوده‏گرى نكند.", 27, 453);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی در به دست آوردن امکانات و رفاهیات چه حرص دارد؟", "ابن ابى عمیر گفت: در طول همنشینی با هشام بن حكم سخنى مفيدتر و نيكوتر از اين سخن درباره عصمت امام  از او نشنیدم. روزى از او پرسيدم: امام بايد معصوم باشد؟ گفت: آرى پرسيدم: صفت عصمت در او چگونه است و از كجا ميتوان فهميد گفت: تمام گناهان داراى چهار وجه است که پنجمى ندارد حرص، حسد، غضب، شهوت اين چهار وجه از امام دور است.نبايد حريص به دنيا باشد با اينكه دنيا زير نگين اوست چون نگهبان مال مسلمانان است پس براى چه حرص بزند. نميتواند حسود باشد زيرا انسان بر ما فوق خود حسد ميبرد و احدى ما فوق او نيست و چطور بر زير دست خود حسد ميورزد.نبايد براى امور دنيا خشم بگيرد مگر اينكه خشم او به واسطه خدا باشد زيرا خداوند اقامه حدود را واجب بر او نموده و نبايد سرزنش كسى در او مؤثر باشد و نباید در اجراى حدود دینی به كسى دلسوزى كند.نبايد پيرو شهوات باشد و دنيا را بر آخرت مقدم دارد زيرا خداوند آخرت را مورد توجه او قرار داده چنانچه دنيا را مورد توجه ما قرار داده است و او متوجه آخرت است همان طور كه ما متوجه دنيائيم آيا كسى چيز بهترى را رها ميكند و روى به چيز بدترى بياورد غذاى خوش مزه‏اى را رها كند و غذاى تلخى بخورد و لباس نرم را رها كند و لباس خشن و زبر بپوشد و نعمت پايدار آخرت را به دنياى فانى و زوال پذير مبادله كند.", 27, 454);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("نظر فرد مدعی نسبت به داشته های خود و دیگران چگونه است؟", "ابن ابى عمیر گفت: در طول همنشینی با هشام بن حكم سخنى مفيدتر و نيكوتر از اين سخن درباره عصمت امام  از او نشنیدم. روزى از او پرسيدم: امام بايد معصوم باشد؟ گفت: آرى پرسيدم: صفت عصمت در او چگونه است و از كجا ميتوان فهميد گفت: تمام گناهان داراى چهار وجه است که پنجمى ندارد حرص، حسد، غضب، شهوت اين چهار وجه از امام دور است.نبايد حريص به دنيا باشد با اينكه دنيا زير نگين اوست چون نگهبان مال مسلمانان است پس براى چه حرص بزند. نميتواند حسود باشد زيرا انسان بر ما فوق خود حسد ميبرد و احدى ما فوق او نيست و چطور بر زير دست خود حسد ميورزد.نبايد براى امور دنيا خشم بگيرد مگر اينكه خشم او به واسطه خدا باشد زيرا خداوند اقامه حدود را واجب بر او نموده و نبايد سرزنش كسى در او مؤثر باشد و نباید در اجراى حدود دینی به كسى دلسوزى كند.نبايد پيرو شهوات باشد و دنيا را بر آخرت مقدم دارد زيرا خداوند آخرت را مورد توجه او قرار داده چنانچه دنيا را مورد توجه ما قرار داده است و او متوجه آخرت است همان طور كه ما متوجه دنيائيم آيا كسى چيز بهترى را رها ميكند و روى به چيز بدترى بياورد غذاى خوش مزه‏اى را رها كند و غذاى تلخى بخورد و لباس نرم را رها كند و لباس خشن و زبر بپوشد و نعمت پايدار آخرت را به دنياى فانى و زوال پذير مبادله كند.", 27, 455);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی هنگام عصیبانیت چه رفتارهایی نشان می دهد؟", "ابن ابى عمیر گفت: در طول همنشینی با هشام بن حكم سخنى مفيدتر و نيكوتر از اين سخن درباره عصمت امام  از او نشنیدم. روزى از او پرسيدم: امام بايد معصوم باشد؟ گفت: آرى پرسيدم: صفت عصمت در او چگونه است و از كجا ميتوان فهميد گفت: تمام گناهان داراى چهار وجه است که پنجمى ندارد حرص، حسد، غضب، شهوت اين چهار وجه از امام دور است.نبايد حريص به دنيا باشد با اينكه دنيا زير نگين اوست چون نگهبان مال مسلمانان است پس براى چه حرص بزند. نميتواند حسود باشد زيرا انسان بر ما فوق خود حسد ميبرد و احدى ما فوق او نيست و چطور بر زير دست خود حسد ميورزد.نبايد براى امور دنيا خشم بگيرد مگر اينكه خشم او به واسطه خدا باشد زيرا خداوند اقامه حدود را واجب بر او نموده و نبايد سرزنش كسى در او مؤثر باشد و نباید در اجراى حدود دینی به كسى دلسوزى كند.نبايد پيرو شهوات باشد و دنيا را بر آخرت مقدم دارد زيرا خداوند آخرت را مورد توجه او قرار داده چنانچه دنيا را مورد توجه ما قرار داده است و او متوجه آخرت است همان طور كه ما متوجه دنيائيم آيا كسى چيز بهترى را رها ميكند و روى به چيز بدترى بياورد غذاى خوش مزه‏اى را رها كند و غذاى تلخى بخورد و لباس نرم را رها كند و لباس خشن و زبر بپوشد و نعمت پايدار آخرت را به دنياى فانى و زوال پذير مبادله كند.", 27, 456);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مورد نظر سلاح پیامبر را به همراه دارد؟", "حارث بن مغيره گفت: به حضرت صادق عليه السّلام عرض كردم:به چه وسيله مى‏توان امام را شناخت؟ فرمود: ... و سلاح پيامبر در نزد اوست... .", 27, 457);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("اطلاعات فرد مدعی در مورد افراد چگونه است؟", "حسن بن علىّ بن فضّال از پدرش از امام رضا عليه السّلام چنين نقل كرده است: امام نشانه¬هایی دارد: عالمترين، حكيم‏ترين، باتقواتر، بردبارتر، شجاعتر، سخى‏تر و عابدترين فرد است، مختون متولّد مى‏شود، پاك و مطهّر است، همان طور كه از روبرو مى‏بيند، از پشت سر نيز مى‏بيند، سايه ندارد، آنگاه كه از شكم مادر به زمين مى‏افتد، بر روى دو كف دست به زمين مى‏افتد و با صداى بلند شهادتين را مى‏گويد، محتلم نمى‏شود، چشمش به خواب مى‏رود ولى قلبش نمى‏خوابد، محدّث است (يعنى صداى فرشته وحى را مى‏شنود ولى آن را نمى‏بيند)، زره رسول خدا صلى اللَّه عليه و آله كاملا اندازه اوست، بول و غائط او ديده نمى‏شود، زيرا خداوند عزّ و جلّ زمين را مأمور كرده تا آنچه را كه از او خارج مى‏شود ببلعد، بوى امام از مشك خوشتر است، اختيار او نسبت به مردم از اختيار آنان نسبت به خودشان بيشتر است، نسبت به مردم از پدر و مادرشان دلسوزتر است، از همه مردم در مقابل خداوند متواضع‏تر است، از همه بيشتر به آنچه كه مردم را به آن امر مى‏كند، عمل مى‏كند و از همه بيشتر، از آنچه كه مردم را از آن نهى مى‏كند دورى مى‏نمايد، دعايش مستجاب است، به گونه‏اى كه اگر دعا كند كه صخره‏اى دو نيم شود، دو نيم خواهد شد، اسلحه حضرت رسول صلى اللَّه عليه و آله و شمشير آن حضرت يعنى ذو الفقار به نزد اوست، نزد او نوشته‏اى است كه اسامى شيعيانشان تا روز قيامت در آن مكتوب است، و نيز نوشته ديگرى دارد كه اسامى دشمنانشان تا روز قيامت در آن آمده است، «جامعه» كه صحيفه‏اى به طول هفتاد ذراع (حدود 35 متر) و مشتمل بر همگى نيازمنديهاى اولاد آدم است نزد اوست، جفر اكبر و اصغر كه پوست بزى و پوست قوچى است و جميع علوم حتّى ارش (مجازات) يك خراش و حتّى زدن يك يا نيم يا ثلث تازيانه در آن ثبت شده و همچنين مصحف فاطمه زهرا- سلام اللَّه عليها- نزد اوست.", 27, 458);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("توانمندی مدعی در پاسخ به نیازهای مردم این دوره چونه است؟", "حسن بن علىّ بن فضّال از پدرش از امام رضا عليه السّلام چنين نقل كرده است: امام نشانه¬هایی دارد: عالمترين، حكيم‏ترين، باتقواتر، بردبارتر، شجاعتر، سخى‏تر و عابدترين فرد است، مختون متولّد مى‏شود، پاك و مطهّر است، همان طور كه از روبرو مى‏بيند، از پشت سر نيز مى‏بيند، سايه ندارد، آنگاه كه از شكم مادر به زمين مى‏افتد، بر روى دو كف دست به زمين مى‏افتد و با صداى بلند شهادتين را مى‏گويد، محتلم نمى‏شود، چشمش به خواب مى‏رود ولى قلبش نمى‏خوابد، محدّث است (يعنى صداى فرشته وحى را مى‏شنود ولى آن را نمى‏بيند)، زره رسول خدا صلى اللَّه عليه و آله كاملا اندازه اوست، بول و غائط او ديده نمى‏شود، زيرا خداوند عزّ و جلّ زمين را مأمور كرده تا آنچه را كه از او خارج مى‏شود ببلعد، بوى امام از مشك خوشتر است، اختيار او نسبت به مردم از اختيار آنان نسبت به خودشان بيشتر است، نسبت به مردم از پدر و مادرشان دلسوزتر است، از همه مردم در مقابل خداوند متواضع‏تر است، از همه بيشتر به آنچه كه مردم را به آن امر مى‏كند، عمل مى‏كند و از همه بيشتر، از آنچه كه مردم را از آن نهى مى‏كند دورى مى‏نمايد، دعايش مستجاب است، به گونه‏اى كه اگر دعا كند كه صخره‏اى دو نيم شود، دو نيم خواهد شد، اسلحه حضرت رسول صلى اللَّه عليه و آله و شمشير آن حضرت يعنى ذو الفقار به نزد اوست، نزد او نوشته‏اى است كه اسامى شيعيانشان تا روز قيامت در آن مكتوب است، و نيز نوشته ديگرى دارد كه اسامى دشمنانشان تا روز قيامت در آن آمده است، «جامعه» كه صحيفه‏اى به طول هفتاد ذراع (حدود 35 متر) و مشتمل بر همگى نيازمنديهاى اولاد آدم است نزد اوست، جفر اكبر و اصغر كه پوست بزى و پوست قوچى است و جميع علوم حتّى ارش (مجازات) يك خراش و حتّى زدن يك يا نيم يا ثلث تازيانه در آن ثبت شده و همچنين مصحف فاطمه زهرا- سلام اللَّه عليها- نزد اوست.", 27, 459);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مورد نظر کتب آسمانی سابق را به همراه دارد؟", "...شما از كجا تورات و انجيل و كتب انبيا را به دست آورده‏ايد؟ فرمود: اين كتابها به وراثت از آنها به ما رسيده، به همان صورتى كه خودشان خوانده‏اند ما مى‏خوانيم بدون كم و كاست... .", 27, 460);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("مدعی علم حروف و اعداد را مسلط است؟", "حسن بن علىّ بن فضّال از پدرش از امام رضا عليه السّلام چنين نقل كرده است: امام نشانه¬هایی دارد: ... جفر اكبر و اصغر كه پوست بزى و پوست قوچى است و جميع علوم حتّى ارش (مجازات) يك خراش و حتّى زدن يك يا نيم يا ثلث تازيانه در آن ثبت شده ... .", 27, 461);
INSERT INTO `questions` (`title`, `descriptions`, `test_id`, `indicator_id`) VALUES ("فرد مورد نظر وصیت الهی را به همراه دارد؟", "سليمان بن مهران از حضرت صادق عليه السّلام نقل كرد: كه ده خصلت از صفات امام است: 1- عصمت. 2- نص و تصريح. 3- اعلم مردم باشد. 4- پرهيزگارترين آنها. 5- داناترين ايشان به كتاب خدا. 6- به او آشكارا وصيت كرده باشند. 7- داراى اعجاز و دليل باشد. 8- چشمش ميخوابد ولى دلش بيدار است. 9- سايه ندارد. 10- از پشت سر چنان مى‏بيند كه از روبرو مشاهده‏ كند.", 27, 462);
